contracts/AttestationRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title AttestationRegistry
 * @notice  - 
 * @dev 
 *    Bond/Challenge 
 *   - Agent  Attestation
 *   -  Bond
 *   -  Challenge Bond
 *   -  Agent
 */
contract AttestationRegistry is AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant ARBITER_ROLE = keccak256("ARBITER_ROLE");

    // 
    address public bondToken;           // Bond  USDT
    uint256 public minBondAmount;       //  Bond 
    uint256 public challengePeriod;     // Challenge 
    uint256 public slashPercentage;     // 10000 = 100%

    struct Attestation {
        address attester;           // Agent
        bytes32 contentHash;        // 
        uint256 bondAmount;         // 
        uint256 timestamp;          // 
        uint256 challengeDeadline;  // Challenge 
        AttestationStatus status;   // 
        address challenger;         // 
        bytes32 challengeReason;    // 
    }

    enum AttestationStatus {
        Pending,        //  Challenge 
        Validated,      // 
        Challenged,     // 
        Slashed,        // 
        Withdrawn       // Bond 
    }

    // Storage
    mapping(bytes32 => Attestation) public attestations;
    bytes32[] public attestationIds;
    
    // 
    mapping(address => uint256) public attesterValidCount;    // 
    mapping(address => uint256) public attesterSlashCount;    // 
    mapping(address => uint256) public challengerWinCount;    // 

    // Events
    event AttestationSubmitted(
        bytes32 indexed attestationId,
        address indexed attester,
        bytes32 contentHash,
        uint256 bondAmount,
        uint256 challengeDeadline
    );

    event AttestationChallenged(
        bytes32 indexed attestationId,
        address indexed challenger,
        bytes32 challengeReason
    );

    event AttestationValidated(bytes32 indexed attestationId);
    event AttestationSlashed(bytes32 indexed attestationId, address indexed challenger, uint256 slashAmount);
    event BondWithdrawn(bytes32 indexed attestationId, address indexed attester, uint256 amount);

    constructor(
        address _admin,
        address _bondToken,
        uint256 _minBondAmount,
        uint256 _challengePeriod,
        uint256 _slashPercentage
    ) {
        require(_bondToken != address(0), "Invalid bond token");
        require(_slashPercentage <= 10000, "Invalid slash percentage");

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(ARBITER_ROLE, _admin);

        bondToken = _bondToken;
        minBondAmount = _minBondAmount;
        challengePeriod = _challengePeriod;
        slashPercentage = _slashPercentage;
    }

    /**
     * @notice 
     * @param contentHash 
     * @param bondAmount  0
     */
    function submitAttestation(
        bytes32 contentHash,
        uint256 bondAmount
    ) external returns (bytes32) {
        require(contentHash != bytes32(0), "Invalid content hash");
        
        bytes32 attestationId = keccak256(
            abi.encodePacked(msg.sender, contentHash, block.timestamp, block.number)
        );
        
        require(attestations[attestationId].timestamp == 0, "Attestation exists");

        //  Bond
        if (bondAmount > 0) {
            require(bondAmount >= minBondAmount, "Bond too low");
            IERC20(bondToken).safeTransferFrom(msg.sender, address(this), bondAmount);
        }

        uint256 deadline = block.timestamp + challengePeriod;

        attestations[attestationId] = Attestation({
            attester: msg.sender,
            contentHash: contentHash,
            bondAmount: bondAmount,
            timestamp: block.timestamp,
            challengeDeadline: deadline,
            status: AttestationStatus.Pending,
            challenger: address(0),
            challengeReason: bytes32(0)
        });

        attestationIds.push(attestationId);

        emit AttestationSubmitted(attestationId, msg.sender, contentHash, bondAmount, deadline);

        return attestationId;
    }

    /**
     * @notice 
     * @param attestationId ID
     * @param reason 
     */
    function challenge(bytes32 attestationId, bytes32 reason) external {
        Attestation storage att = attestations[attestationId];
        
        require(att.timestamp != 0, "Attestation not found");
        require(att.status == AttestationStatus.Pending, "Not challengeable");
        require(block.timestamp <= att.challengeDeadline, "Challenge period ended");
        require(msg.sender != att.attester, "Cannot self-challenge");

        att.status = AttestationStatus.Challenged;
        att.challenger = msg.sender;
        att.challengeReason = reason;

        emit AttestationChallenged(attestationId, msg.sender, reason);
    }

    /**
     * @notice 
     * @param attestationId ID
     * @param challengeSucceeded 
     */
    function arbitrate(
        bytes32 attestationId,
        bool challengeSucceeded
    ) external onlyRole(ARBITER_ROLE) {
        Attestation storage att = attestations[attestationId];
        
        require(att.status == AttestationStatus.Challenged, "Not in challenge");

        if (challengeSucceeded) {
            att.status = AttestationStatus.Slashed;
            attesterSlashCount[att.attester]++;
            challengerWinCount[att.challenger]++;

            // 
            uint256 slashAmount = (att.bondAmount * slashPercentage) / 10000;
            uint256 refundAmount = att.bondAmount - slashAmount;

            // 
            if (slashAmount > 0) {
                IERC20(bondToken).safeTransfer(att.challenger, slashAmount);
            }

            // 
            if (refundAmount > 0) {
                IERC20(bondToken).safeTransfer(att.attester, refundAmount);
            }

            emit AttestationSlashed(attestationId, att.challenger, slashAmount);
        } else {
            att.status = AttestationStatus.Validated;
            attesterValidCount[att.attester]++;
            emit AttestationValidated(attestationId);
        }
    }

    /**
     * @notice Challenge 
     */
    function validate(bytes32 attestationId) external {
        Attestation storage att = attestations[attestationId];
        
        require(att.status == AttestationStatus.Pending, "Not pending");
        require(block.timestamp > att.challengeDeadline, "Challenge period not ended");

        att.status = AttestationStatus.Validated;
        attesterValidCount[att.attester]++;

        emit AttestationValidated(attestationId);
    }

    /**
     * @notice  Bond
     */
    function withdrawBond(bytes32 attestationId) external {
        Attestation storage att = attestations[attestationId];
        
        require(msg.sender == att.attester, "Not attester");
        require(att.status == AttestationStatus.Validated, "Not validated");
        require(att.bondAmount > 0, "No bond");

        uint256 amount = att.bondAmount;
        att.bondAmount = 0;
        att.status = AttestationStatus.Withdrawn;

        IERC20(bondToken).safeTransfer(msg.sender, amount);

        emit BondWithdrawn(attestationId, msg.sender, amount);
    }

    /**
     * @notice 
     */
    function getAttestation(bytes32 attestationId) external view returns (Attestation memory) {
        return attestations[attestationId];
    }

    /**
     * @notice 
     */
    function getAttesterStats(address attester) external view returns (
        uint256 validCount,
        uint256 slashCount
    ) {
        return (attesterValidCount[attester], attesterSlashCount[attester]);
    }

    /**
     * @notice 
     */
    function getTotalAttestations() external view returns (uint256) {
        return attestationIds.length;
    }

    // Admin functions
    function setMinBondAmount(uint256 _amount) external onlyRole(DEFAULT_ADMIN_ROLE) {
        minBondAmount = _amount;
    }

    function setChallengePeriod(uint256 _period) external onlyRole(DEFAULT_ADMIN_ROLE) {
        challengePeriod = _period;
    }

    function setSlashPercentage(uint256 _percentage) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_percentage <= 10000, "Invalid percentage");
        slashPercentage = _percentage;
    }
}

contracts/EpochManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title EpochManager
 * @notice Epoch  - 
 * @dev 
 *   -  Epoch  7 
 *   - Epoch  MerkleDistributor 
 *   - 
 */
contract EpochManager is AccessControl {
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");

    struct Epoch {
        uint256 id;
        uint256 startTime;
        uint256 endTime;
        bytes32 merkleRoot;      //  Epoch  Merkle Root
        bool finalized;          // 
        uint256 totalRewards;    //  Epoch 
    }

    // Configuration
    uint256 public epochDuration = 7 days;
    uint256 public minEpochDuration = 1 days;
    uint256 public maxEpochDuration = 30 days;

    // State
    uint256 public currentEpochId;
    mapping(uint256 => Epoch) public epochs;
    
    // Events
    event EpochStarted(uint256 indexed epochId, uint256 startTime, uint256 endTime);
    event EpochFinalized(uint256 indexed epochId, bytes32 merkleRoot, uint256 totalRewards);
    event EpochDurationUpdated(uint256 oldDuration, uint256 newDuration);

    constructor(address _admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(OPERATOR_ROLE, _admin);

        //  Epoch
        currentEpochId = 1;
        epochs[currentEpochId] = Epoch({
            id: currentEpochId,
            startTime: block.timestamp,
            endTime: block.timestamp + epochDuration,
            merkleRoot: bytes32(0),
            finalized: false,
            totalRewards: 0
        });

        emit EpochStarted(currentEpochId, block.timestamp, block.timestamp + epochDuration);
    }

    /**
     * @notice  Epoch ID
     * @dev  Epoch  ID
     */
    function getCurrentEpochId() external view returns (uint256) {
        return currentEpochId;
    }

    /**
     * @notice  Epoch 
     */
    function getCurrentEpoch() external view returns (Epoch memory) {
        return epochs[currentEpochId];
    }

    /**
     * @notice  Epoch
     */
    function canAdvanceEpoch() public view returns (bool) {
        return block.timestamp >= epochs[currentEpochId].endTime;
    }

    /**
     * @notice  Epoch
     * @dev 
     */
    function advanceEpoch() external {
        require(canAdvanceEpoch(), "Current epoch not ended");
        require(!epochs[currentEpochId].finalized, "Current epoch not finalized yet");

        //  Epoch
        currentEpochId++;
        
        epochs[currentEpochId] = Epoch({
            id: currentEpochId,
            startTime: block.timestamp,
            endTime: block.timestamp + epochDuration,
            merkleRoot: bytes32(0),
            finalized: false,
            totalRewards: 0
        });

        emit EpochStarted(currentEpochId, block.timestamp, block.timestamp + epochDuration);
    }

    /**
     * @notice  Epoch  Merkle Root
     * @dev  OPERATOR_ROLE 
     */
    function finalizeEpoch(
        uint256 epochId,
        bytes32 merkleRoot,
        uint256 totalRewards
    ) external onlyRole(OPERATOR_ROLE) {
        require(epochId <= currentEpochId, "Invalid epoch");
        require(!epochs[epochId].finalized, "Already finalized");
        require(merkleRoot != bytes32(0), "Invalid merkle root");

        epochs[epochId].merkleRoot = merkleRoot;
        epochs[epochId].totalRewards = totalRewards;
        epochs[epochId].finalized = true;

        emit EpochFinalized(epochId, merkleRoot, totalRewards);
    }

    /**
     * @notice  Epoch 
     */
    function setEpochDuration(uint256 _duration) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_duration >= minEpochDuration, "Duration too short");
        require(_duration <= maxEpochDuration, "Duration too long");
        
        uint256 oldDuration = epochDuration;
        epochDuration = _duration;
        
        emit EpochDurationUpdated(oldDuration, _duration);
    }

    /**
     * @notice  Epoch  Merkle Root
     */
    function getMerkleRoot(uint256 epochId) external view returns (bytes32) {
        return epochs[epochId].merkleRoot;
    }

    /**
     * @notice  Epoch 
     */
    function isEpochFinalized(uint256 epochId) external view returns (bool) {
        return epochs[epochId].finalized;
    }
}

contracts/MerkleDistributor.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/MerkleProof.sol";

/**
 * @title MerkleDistributor
 * @notice Merkle  -  Epoch 
 * @dev 
 *   -  Merkle 
 *   -  Epoch 
 *   - 
 */
contract MerkleDistributor is AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");

    // Treasury 
    address public treasury;

    // Epoch Merkle Roots (epochId => token => merkleRoot)
    mapping(uint256 => mapping(address => bytes32)) public epochMerkleRoots;

    //  (epochId => token => account => claimed)
    mapping(uint256 => mapping(address => mapping(address => bool))) public claimed;

    //  (account => token => totalClaimed)
    mapping(address => mapping(address => uint256)) public totalClaimed;

    // Events
    event MerkleRootSet(uint256 indexed epochId, address indexed token, bytes32 merkleRoot);
    event Claimed(
        uint256 indexed epochId,
        address indexed account,
        address indexed token,
        uint256 amount
    );
    event TreasuryUpdated(address oldTreasury, address newTreasury);

    constructor(address _admin, address _treasury) {
        require(_treasury != address(0), "Invalid treasury");
        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(OPERATOR_ROLE, _admin);
        treasury = _treasury;
    }

    /**
     * @notice  Epoch  Merkle Root
     * @dev  OPERATOR  EpochManager 
     */
    function setMerkleRoot(
        uint256 epochId,
        address token,
        bytes32 merkleRoot
    ) external onlyRole(OPERATOR_ROLE) {
        require(merkleRoot != bytes32(0), "Invalid merkle root");
        epochMerkleRoots[epochId][token] = merkleRoot;
        emit MerkleRootSet(epochId, token, merkleRoot);
    }

    /**
     * @notice  Epoch 
     * @param epochId Epoch ID
     * @param token 
     * @param amount 
     * @param merkleProof Merkle 
     */
    function claim(
        uint256 epochId,
        address token,
        uint256 amount,
        bytes32[] calldata merkleProof
    ) external {
        require(!claimed[epochId][token][msg.sender], "Already claimed");
        
        bytes32 merkleRoot = epochMerkleRoots[epochId][token];
        require(merkleRoot != bytes32(0), "Merkle root not set");

        //  Merkle 
        bytes32 leaf = keccak256(abi.encodePacked(msg.sender, amount));
        require(MerkleProof.verify(merkleProof, merkleRoot, leaf), "Invalid proof");

        // 
        claimed[epochId][token][msg.sender] = true;
        totalClaimed[msg.sender][token] += amount;

        //  Treasury 
        IERC20(token).safeTransferFrom(treasury, msg.sender, amount);

        emit Claimed(epochId, msg.sender, token, amount);
    }

    /**
     * @notice  Epoch 
     * @param epochIds Epoch ID 
     * @param token 
     * @param amounts 
     * @param merkleProofs Merkle 
     */
    function claimMultiple(
        uint256[] calldata epochIds,
        address token,
        uint256[] calldata amounts,
        bytes32[][] calldata merkleProofs
    ) external {
        require(epochIds.length == amounts.length, "Length mismatch");
        require(epochIds.length == merkleProofs.length, "Length mismatch");

        uint256 totalAmount = 0;

        for (uint256 i = 0; i < epochIds.length; i++) {
            uint256 epochId = epochIds[i];
            uint256 amount = amounts[i];
            
            if (claimed[epochId][token][msg.sender]) {
                continue; // 
            }

            bytes32 merkleRoot = epochMerkleRoots[epochId][token];
            if (merkleRoot == bytes32(0)) {
                continue; // 
            }

            bytes32 leaf = keccak256(abi.encodePacked(msg.sender, amount));
            if (!MerkleProof.verify(merkleProofs[i], merkleRoot, leaf)) {
                continue; // 
            }

            claimed[epochId][token][msg.sender] = true;
            totalAmount += amount;

            emit Claimed(epochId, msg.sender, token, amount);
        }

        if (totalAmount > 0) {
            totalClaimed[msg.sender][token] += totalAmount;
            IERC20(token).safeTransferFrom(treasury, msg.sender, totalAmount);
        }
    }

    /**
     * @notice 
     */
    function isClaimed(
        uint256 epochId,
        address token,
        address account
    ) external view returns (bool) {
        return claimed[epochId][token][account];
    }

    /**
     * @notice 
     */
    function canClaim(
        uint256 epochId,
        address token,
        address account,
        uint256 amount,
        bytes32[] calldata merkleProof
    ) external view returns (bool) {
        if (claimed[epochId][token][account]) {
            return false;
        }

        bytes32 merkleRoot = epochMerkleRoots[epochId][token];
        if (merkleRoot == bytes32(0)) {
            return false;
        }

        bytes32 leaf = keccak256(abi.encodePacked(account, amount));
        return MerkleProof.verify(merkleProof, merkleRoot, leaf);
    }

    /**
     * @notice  Treasury 
     */
    function setTreasury(address _treasury) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_treasury != address(0), "Invalid treasury");
        address oldTreasury = treasury;
        treasury = _treasury;
        emit TreasuryUpdated(oldTreasury, _treasury);
    }
}

contracts/ReceiptRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";

/**
 * @title ReceiptRegistry
 * @notice  -  x402 
 * @dev 
 *   1.  Indexer 
 *   2. Epoch 
 *   3. 
 */
contract ReceiptRegistry is AccessControl {
    bytes32 public constant EMITTER_ROLE = keccak256("EMITTER_ROLE");

    struct Receipt {
        bytes32 paymentId;       // ID
        address payer;           // 
        address merchant;        // 
        address agent;           // Agent
        address token;           // 
        uint256 amount;          // 
        uint256 protocolFee;     //  (0.3%)
        uint256 timestamp;       // 
        uint256 epochId;         // Epoch
        bytes32 routeRefHash;    // 
    }

    // Storage
    mapping(bytes32 => Receipt) public receipts;
    bytes32[] public receiptIds;
    
    // Epoch tracking
    mapping(uint256 => bytes32[]) public epochReceipts;
    mapping(uint256 => uint256) public epochTotalVolume;
    mapping(uint256 => uint256) public epochTotalFees;

    // Events
    event ReceiptCreated(
        bytes32 indexed paymentId,
        address indexed payer,
        address indexed merchant,
        address agent,
        address token,
        uint256 amount,
        uint256 protocolFee,
        uint256 epochId,
        bytes32 routeRefHash
    );

    event EpochFinalized(
        uint256 indexed epochId,
        uint256 totalReceipts,
        uint256 totalVolume,
        uint256 totalFees
    );

    constructor(address _admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(EMITTER_ROLE, _admin);
    }

    /**
     * @notice 
     * @dev  ArnFeeSplitter 
     */
    function recordReceipt(
        bytes32 paymentId,
        address payer,
        address merchant,
        address agent,
        address token,
        uint256 amount,
        uint256 protocolFee,
        uint256 epochId,
        bytes32 routeRefHash
    ) external onlyRole(EMITTER_ROLE) {
        require(receipts[paymentId].timestamp == 0, "Receipt already exists");

        receipts[paymentId] = Receipt({
            paymentId: paymentId,
            payer: payer,
            merchant: merchant,
            agent: agent,
            token: token,
            amount: amount,
            protocolFee: protocolFee,
            timestamp: block.timestamp,
            epochId: epochId,
            routeRefHash: routeRefHash
        });

        receiptIds.push(paymentId);
        epochReceipts[epochId].push(paymentId);
        epochTotalVolume[epochId] += amount;
        epochTotalFees[epochId] += protocolFee;

        emit ReceiptCreated(
            paymentId,
            payer,
            merchant,
            agent,
            token,
            amount,
            protocolFee,
            epochId,
            routeRefHash
        );
    }

    /**
     * @notice 
     */
    function getReceipt(bytes32 paymentId) external view returns (Receipt memory) {
        return receipts[paymentId];
    }

    /**
     * @notice EpochID
     */
    function getEpochReceiptIds(uint256 epochId) external view returns (bytes32[] memory) {
        return epochReceipts[epochId];
    }

    /**
     * @notice Epoch
     */
    function getEpochStats(uint256 epochId) external view returns (
        uint256 totalReceipts,
        uint256 totalVolume,
        uint256 totalFees
    ) {
        return (
            epochReceipts[epochId].length,
            epochTotalVolume[epochId],
            epochTotalFees[epochId]
        );
    }

    /**
     * @notice 
     */
    function getTotalReceipts() external view returns (uint256) {
        return receiptIds.length;
    }
}

