contracts/ArnFeeSplitter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title ArnFeeSplitter
 * @notice 核心入口合约：处理 x402 支付的分账逻辑
 * @dev 接收支付 -> 扣除 Protocol Fee -> 转发剩余资金给商户
 */
contract ArnFeeSplitter is Ownable {
    using SafeERC20 for IERC20;

    // Protocol Fee (Basis Points, 10000 = 100%)
    // Default: 0.3% (30 bps)
    uint256 public protocolFeeBps = 30;
    
    // Treasury Address (DAO Pool)
    address public treasury;

    event PaymentSplit(
        address indexed token,
        address indexed merchant,
        uint256 totalAmount,
        uint256 merchantAmount,
        uint256 feeAmount,
        bytes32 indexed routeRefHash
    );

    event ProtocolFeeUpdated(uint256 newFeeBps);
    event TreasuryUpdated(address newTreasury);

    constructor(address _treasury) Ownable(msg.sender) {
        require(_treasury != address(0), "Invalid treasury");
        treasury = _treasury;
    }

    /**
     * @notice 处理 ERC20 支付分账
     * @param token 支付代币地址
     * @param merchant 商户接收地址
     * @param amount 支付总金额
     * @param routeRef 归因数据 (用于链下积分计算)
     */
    function splitPaymentERC20(
        address token,
        address merchant,
        uint256 amount,
        bytes calldata routeRef
    ) external {
        require(amount > 0, "Amount must be > 0");
        
        // 1. Calculate Fee
        uint256 feeAmount = (amount * protocolFeeBps) / 10000;
        uint256 merchantAmount = amount - feeAmount;

        // 2. Transfer Fee to Treasury
        if (feeAmount > 0) {
            IERC20(token).safeTransferFrom(msg.sender, treasury, feeAmount);
        }

        // 3. Transfer Remaining to Merchant
        if (merchantAmount > 0) {
            IERC20(token).safeTransferFrom(msg.sender, merchant, merchantAmount);
        }

        // 4. Emit Event for Indexer
        emit PaymentSplit(
            token,
            merchant,
            amount,
            merchantAmount,
            feeAmount,
            keccak256(routeRef)
        );
    }

    /**
     * @notice 处理原生代币 (ETH/MATIC) 支付分账
     */
    function splitPaymentNative(
        address merchant,
        bytes calldata routeRef
    ) external payable {
        uint256 amount = msg.value;
        require(amount > 0, "Amount must be > 0");

        // 1. Calculate Fee
        uint256 feeAmount = (amount * protocolFeeBps) / 10000;
        uint256 merchantAmount = amount - feeAmount;

        // 2. Transfer Fee to Treasury
        if (feeAmount > 0) {
            (bool successFee, ) = treasury.call{value: feeAmount}("");
            require(successFee, "Fee transfer failed");
        }

        // 3. Transfer Remaining to Merchant
        if (merchantAmount > 0) {
            (bool successMerch, ) = merchant.call{value: merchantAmount}("");
            require(successMerch, "Merchant transfer failed");
        }

        emit PaymentSplit(
            address(0),
            merchant,
            amount,
            merchantAmount,
            feeAmount,
            keccak256(routeRef)
        );
    }

    // --- Admin Functions ---

    function setProtocolFee(uint256 _bps) external onlyOwner {
        require(_bps <= 1000, "Fee too high (max 10%)");
        protocolFeeBps = _bps;
        emit ProtocolFeeUpdated(_bps);
    }

    function setTreasury(address _treasury) external onlyOwner {
        require(_treasury != address(0), "Invalid address");
        treasury = _treasury;
        emit TreasuryUpdated(_treasury);
    }
}

contracts/ArnTreasury.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title ArnTreasury
 * @notice ARN 生态金库
 * @dev 资金只进不出，除非通过 Timelock 或 Distributor 授权
 */
contract ArnTreasury is AccessControl {
    using SafeERC20 for IERC20;

    bytes32 public constant DISTRIBUTOR_ROLE = keccak256("DISTRIBUTOR_ROLE");
    bytes32 public constant TIMELOCK_ROLE = keccak256("TIMELOCK_ROLE");

    event FundsWithdrawn(address indexed token, address indexed to, uint256 amount, string reason);
    event FundsReceived(address indexed sender, uint256 amount);

    constructor(address _admin) {
        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(TIMELOCK_ROLE, _admin); // 初期 Admin 兼任 Timelock，后期移交
    }

    // 允许接收原生代币
    receive() external payable {
        emit FundsReceived(msg.sender, msg.value);
    }

    /**
     * @notice 提取资金 (仅限授权角色)
     */
    function withdraw(
        address token,
        address to,
        uint256 amount,
        string calldata reason
    ) external onlyRole(TIMELOCK_ROLE) {
        require(to != address(0), "Invalid recipient");
        
        if (token == address(0)) {
            (bool success, ) = to.call{value: amount}("");
            require(success, "Transfer failed");
        } else {
            IERC20(token).safeTransfer(to, amount);
        }

        emit FundsWithdrawn(token, to, amount, reason);
    }

    /**
     * @notice 授权 Distributor 提取资金 (用于自动分发)
     * @dev 这是一个特殊的 withdraw，通常由 MerkleDistributor 合约调用
     */
    function distributeReward(
        address token,
        address to,
        uint256 amount
    ) external onlyRole(DISTRIBUTOR_ROLE) {
        if (token == address(0)) {
            (bool success, ) = to.call{value: amount}("");
            require(success, "Transfer failed");
        } else {
            IERC20(token).safeTransfer(to, amount);
        }
        // 不触发通用 Withdraw 事件，避免噪音，Distributor 自己会发 Claim 事件
    }
}

