contracts/AuditProof.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";

/**
 * @title AuditProof
 * @dev 结果审计与托管释放合约
 * 
 * 实现"任务完成"的链上验证：
 * 1. 托管资金：用户预存资金到合约
 * 2. 任务绑定：关联 proofHash（预期结果哈希）
 * 3. 验证释放：Auditor 或用户确认结果后释放资金
 * 
 * 支持三种验证模式：
 * - SIGNATURE: 指定 Auditor 签名验证
 * - HASH_MATCH: 结果哈希匹配验证
 * - MULTISIG: 多签验证（M-of-N）
 */
contract AuditProof is Ownable, ReentrancyGuard, Pausable, EIP712 {
    using SafeERC20 for IERC20;
    using ECDSA for bytes32;

    // ============ 常量 ============
    bytes32 public constant RELEASE_TYPEHASH = keccak256(
        "Release(bytes32 taskId,bytes32 resultHash,uint256 quality,uint256 nonce)"
    );
    
    uint256 public constant MAX_AUDITORS = 10;
    uint256 public constant MIN_QUALITY_SCORE = 60; // 最低质量分（0-100）
    uint256 public constant DISPUTE_WINDOW = 24 hours; // 争议窗口期

    // ============ 枚举 ============
    enum VerificationMode {
        SIGNATURE,      // 签名验证
        HASH_MATCH,     // 哈希匹配
        MULTISIG        // 多签验证
    }

    enum TaskStatus {
        CREATED,        // 已创建
        FUNDED,         // 已托管资金
        SUBMITTED,      // 已提交结果
        VERIFIED,       // 已验证
        RELEASED,       // 已释放资金
        DISPUTED,       // 争议中
        REFUNDED        // 已退款
    }

    // ============ 结构体 ============
    
    /**
     * @dev 任务配置 - 简化版本避免 Stack too deep
     */
    struct TaskConfig {
        bytes32 taskId;              // 任务 ID
        address creator;             // 任务创建者（付款方）
        address executor;            // 执行者（收款方）
        
        // 资金信息
        address token;               // 支付代币
        uint256 amount;              // 托管金额
        uint256 platformFee;         // 平台费用
        
        // 验证配置
        VerificationMode mode;       // 验证模式
        bytes32 expectedProofHash;   // 预期结果哈希（用于 HASH_MATCH 模式）
        uint256 requiredSignatures;  // 所需签名数（用于 MULTISIG 模式）
        
        // 状态
        TaskStatus status;
        uint256 createdAt;
        uint256 deadline;            // 任务截止时间
        uint256 releasedAt;
        
        // 结果
        bytes32 submittedResultHash; // 提交的结果哈希
        uint256 qualityScore;        // 质量评分 (0-100)
    }

    /**
     * @dev 签名记录
     */
    struct SignatureRecord {
        address signer;
        bytes32 resultHash;
        uint256 quality;
        uint256 timestamp;
    }

    // ============ 状态变量 ============
    
    mapping(bytes32 => TaskConfig) public tasks;
    mapping(bytes32 => address[]) public taskAuditors;  // 分离存储审计员列表
    mapping(bytes32 => SignatureRecord[]) public taskSignatures;
    mapping(bytes32 => mapping(address => bool)) public hasSignedTask;
    mapping(address => bool) public trustedAuditors;
    
    // 平台配置
    address public platformTreasury;
    uint256 public defaultPlatformFeeRate = 100; // 1% = 100 basis points
    uint256 public nonce;

    // ============ 事件 ============
    
    event TaskCreated(
        bytes32 indexed taskId,
        address indexed creator,
        address indexed executor,
        uint256 amount,
        VerificationMode mode
    );
    
    event TaskFunded(
        bytes32 indexed taskId,
        address indexed funder,
        uint256 amount
    );
    
    event ResultSubmitted(
        bytes32 indexed taskId,
        address indexed executor,
        bytes32 resultHash
    );
    
    event ProofVerified(
        bytes32 indexed taskId,
        address indexed auditor,
        bytes32 resultHash,
        uint256 quality
    );
    
    event FundsReleased(
        bytes32 indexed taskId,
        address indexed executor,
        uint256 amount,
        uint256 platformFee
    );
    
    event TaskDisputed(
        bytes32 indexed taskId,
        address indexed disputer,
        string reason
    );
    
    event TaskRefunded(
        bytes32 indexed taskId,
        address indexed creator,
        uint256 amount
    );

    event AuditorUpdated(address indexed auditor, bool trusted);

    // ============ 构造函数 ============
    
    constructor(address _treasury) EIP712("AuditProof", "1") {
        platformTreasury = _treasury;
    }

    // ============ 管理函数 ============

    function setTrustedAuditor(address auditor, bool trusted) external onlyOwner {
        trustedAuditors[auditor] = trusted;
        emit AuditorUpdated(auditor, trusted);
    }

    function setPlatformTreasury(address treasury) external onlyOwner {
        require(treasury != address(0), "Invalid treasury");
        platformTreasury = treasury;
    }

    function setDefaultPlatformFeeRate(uint256 rate) external onlyOwner {
        require(rate <= 1000, "Fee rate too high"); // Max 10%
        defaultPlatformFeeRate = rate;
    }

    // ============ 核心函数 ============

    /**
     * @dev 创建托管任务
     */
    function createTask(
        bytes32 taskId,
        address executor,
        address token,
        uint256 amount,
        VerificationMode mode,
        bytes32 expectedProofHash,
        address[] calldata auditors,
        uint256 requiredSignatures,
        uint256 deadline
    ) external whenNotPaused returns (bytes32) {
        require(tasks[taskId].taskId == bytes32(0), "Task exists");
        require(executor != address(0), "Invalid executor");
        require(amount > 0, "Invalid amount");
        require(deadline > block.timestamp, "Invalid deadline");
        
        // 验证模式特定检查
        if (mode == VerificationMode.HASH_MATCH) {
            require(expectedProofHash != bytes32(0), "Hash required");
        } else if (mode == VerificationMode.SIGNATURE) {
            require(auditors.length > 0 && auditors.length <= MAX_AUDITORS, "Invalid auditors");
        } else if (mode == VerificationMode.MULTISIG) {
            require(auditors.length >= requiredSignatures, "Invalid multisig config");
            require(requiredSignatures > 0, "Need at least 1 signature");
        }

        uint256 platformFee = (amount * defaultPlatformFeeRate) / 10000;

        TaskConfig storage task = tasks[taskId];
        task.taskId = taskId;
        task.creator = msg.sender;
        task.executor = executor;
        task.token = token;
        task.amount = amount;
        task.platformFee = platformFee;
        task.mode = mode;
        task.expectedProofHash = expectedProofHash;
        task.requiredSignatures = requiredSignatures;
        task.status = TaskStatus.CREATED;
        task.createdAt = block.timestamp;
        task.deadline = deadline;
        
        // 分离存储审计员列表
        for (uint i = 0; i < auditors.length; i++) {
            taskAuditors[taskId].push(auditors[i]);
        }

        emit TaskCreated(taskId, msg.sender, executor, amount, mode);
        return taskId;
    }

    /**
     * @dev 为任务注资（托管）
     */
    function fundTask(bytes32 taskId) external payable whenNotPaused nonReentrant {
        TaskConfig storage task = tasks[taskId];
        require(task.taskId != bytes32(0), "Task not found");
        require(task.status == TaskStatus.CREATED, "Invalid status");
        
        uint256 totalRequired = task.amount + task.platformFee;
        
        if (task.token == address(0)) {
            // ETH 支付
            require(msg.value >= totalRequired, "Insufficient ETH");
            // 退还多余的 ETH
            if (msg.value > totalRequired) {
                payable(msg.sender).transfer(msg.value - totalRequired);
            }
        } else {
            // ERC20 支付
            IERC20(task.token).safeTransferFrom(msg.sender, address(this), totalRequired);
        }

        task.status = TaskStatus.FUNDED;
        emit TaskFunded(taskId, msg.sender, totalRequired);
    }

    /**
     * @dev 提交任务结果（执行者调用）
     */
    function submitResult(
        bytes32 taskId,
        bytes32 resultHash
    ) external whenNotPaused {
        TaskConfig storage task = tasks[taskId];
        require(task.taskId != bytes32(0), "Task not found");
        require(task.status == TaskStatus.FUNDED, "Task not funded");
        require(msg.sender == task.executor, "Not executor");
        require(block.timestamp <= task.deadline, "Task expired");

        task.submittedResultHash = resultHash;
        task.status = TaskStatus.SUBMITTED;

        emit ResultSubmitted(taskId, msg.sender, resultHash);

        // 如果是哈希匹配模式，自动验证
        if (task.mode == VerificationMode.HASH_MATCH) {
            if (resultHash == task.expectedProofHash) {
                _verifyAndRelease(taskId, 100); // 完全匹配给 100 分
            }
        }
    }

    /**
     * @dev 审计员验证结果
     */
    function verifyResult(
        bytes32 taskId,
        bytes32 resultHash,
        uint256 quality,
        bytes calldata signature
    ) external whenNotPaused {
        TaskConfig storage task = tasks[taskId];
        require(task.taskId != bytes32(0), "Task not found");
        require(task.status == TaskStatus.SUBMITTED, "Not submitted");
        require(!hasSignedTask[taskId][msg.sender], "Already signed");
        require(quality <= 100, "Invalid quality");

        // 验证签名
        bytes32 structHash = keccak256(abi.encode(
            RELEASE_TYPEHASH,
            taskId,
            resultHash,
            quality,
            nonce++
        ));
        bytes32 digest = _hashTypedDataV4(structHash);
        address signer = digest.recover(signature);

        // 检查签名者权限
        bool isValidSigner = false;
        address[] storage auditors = taskAuditors[taskId];
        if (task.mode == VerificationMode.SIGNATURE || task.mode == VerificationMode.MULTISIG) {
            for (uint i = 0; i < auditors.length; i++) {
                if (auditors[i] == signer) {
                    isValidSigner = true;
                    break;
                }
            }
        }
        // 也允许受信任的全局审计员
        if (trustedAuditors[signer]) {
            isValidSigner = true;
        }
        require(isValidSigner, "Invalid signer");

        // 记录签名
        hasSignedTask[taskId][signer] = true;
        taskSignatures[taskId].push(SignatureRecord({
            signer: signer,
            resultHash: resultHash,
            quality: quality,
            timestamp: block.timestamp
        }));

        emit ProofVerified(taskId, signer, resultHash, quality);

        // 检查是否达到释放条件
        if (task.mode == VerificationMode.SIGNATURE) {
            // 单签名模式：任意一个审计员签名即可
            if (quality >= MIN_QUALITY_SCORE) {
                _verifyAndRelease(taskId, quality);
            }
        } else if (task.mode == VerificationMode.MULTISIG) {
            // 多签模式：需要足够数量的签名
            if (taskSignatures[taskId].length >= task.requiredSignatures) {
                // 计算平均质量分
                uint256 totalQuality = 0;
                for (uint i = 0; i < taskSignatures[taskId].length; i++) {
                    totalQuality += taskSignatures[taskId][i].quality;
                }
                uint256 avgQuality = totalQuality / taskSignatures[taskId].length;
                if (avgQuality >= MIN_QUALITY_SCORE) {
                    _verifyAndRelease(taskId, avgQuality);
                }
            }
        }
    }

    /**
     * @dev 用户直接确认释放（创建者调用）
     */
    function confirmRelease(bytes32 taskId, uint256 quality) external whenNotPaused {
        TaskConfig storage task = tasks[taskId];
        require(task.taskId != bytes32(0), "Task not found");
        require(msg.sender == task.creator, "Not creator");
        require(task.status == TaskStatus.SUBMITTED || task.status == TaskStatus.FUNDED, "Invalid status");

        _verifyAndRelease(taskId, quality);
    }

    /**
     * @dev 发起争议
     */
    function disputeTask(bytes32 taskId, string calldata reason) external whenNotPaused {
        TaskConfig storage task = tasks[taskId];
        require(task.taskId != bytes32(0), "Task not found");
        require(msg.sender == task.creator || msg.sender == task.executor, "Not participant");
        require(
            task.status == TaskStatus.SUBMITTED || task.status == TaskStatus.VERIFIED,
            "Cannot dispute"
        );

        task.status = TaskStatus.DISPUTED;
        emit TaskDisputed(taskId, msg.sender, reason);
    }

    /**
     * @dev 解决争议（仅 Owner）
     */
    function resolveDispute(
        bytes32 taskId,
        bool releaseToExecutor,
        uint256 executorShare // basis points
    ) external onlyOwner {
        TaskConfig storage task = tasks[taskId];
        require(task.status == TaskStatus.DISPUTED, "Not disputed");
        require(executorShare <= 10000, "Invalid share");

        if (releaseToExecutor) {
            uint256 executorAmount = (task.amount * executorShare) / 10000;
            uint256 creatorRefund = task.amount - executorAmount;

            if (task.token == address(0)) {
                if (executorAmount > 0) payable(task.executor).transfer(executorAmount);
                if (creatorRefund > 0) payable(task.creator).transfer(creatorRefund);
                payable(platformTreasury).transfer(task.platformFee);
            } else {
                if (executorAmount > 0) IERC20(task.token).safeTransfer(task.executor, executorAmount);
                if (creatorRefund > 0) IERC20(task.token).safeTransfer(task.creator, creatorRefund);
                IERC20(task.token).safeTransfer(platformTreasury, task.platformFee);
            }
        } else {
            // 全额退款给创建者
            uint256 refund = task.amount + task.platformFee;
            if (task.token == address(0)) {
                payable(task.creator).transfer(refund);
            } else {
                IERC20(task.token).safeTransfer(task.creator, refund);
            }
        }

        task.status = TaskStatus.RELEASED;
    }

    /**
     * @dev 过期退款
     */
    function refundExpired(bytes32 taskId) external whenNotPaused nonReentrant {
        TaskConfig storage task = tasks[taskId];
        require(task.taskId != bytes32(0), "Task not found");
        require(msg.sender == task.creator, "Not creator");
        require(block.timestamp > task.deadline + DISPUTE_WINDOW, "Not expired");
        require(
            task.status == TaskStatus.FUNDED || task.status == TaskStatus.SUBMITTED,
            "Cannot refund"
        );

        uint256 refund = task.amount + task.platformFee;
        task.status = TaskStatus.REFUNDED;

        if (task.token == address(0)) {
            payable(task.creator).transfer(refund);
        } else {
            IERC20(task.token).safeTransfer(task.creator, refund);
        }

        emit TaskRefunded(taskId, task.creator, refund);
    }

    // ============ 内部函数 ============

    function _verifyAndRelease(bytes32 taskId, uint256 quality) internal nonReentrant {
        TaskConfig storage task = tasks[taskId];
        require(task.status != TaskStatus.RELEASED, "Already released");
        require(task.status != TaskStatus.REFUNDED, "Already refunded");

        task.status = TaskStatus.VERIFIED;
        task.qualityScore = quality;

        // 分发资金
        if (task.token == address(0)) {
            // ETH
            payable(task.executor).transfer(task.amount);
            payable(platformTreasury).transfer(task.platformFee);
        } else {
            // ERC20
            IERC20(task.token).safeTransfer(task.executor, task.amount);
            IERC20(task.token).safeTransfer(platformTreasury, task.platformFee);
        }

        task.status = TaskStatus.RELEASED;
        task.releasedAt = block.timestamp;

        emit FundsReleased(taskId, task.executor, task.amount, task.platformFee);
    }

    // ============ 视图函数 ============

    function getTask(bytes32 taskId) external view returns (TaskConfig memory) {
        return tasks[taskId];
    }

    function getTaskSignatures(bytes32 taskId) external view returns (SignatureRecord[] memory) {
        return taskSignatures[taskId];
    }

    function isAuditorForTask(bytes32 taskId, address auditor) external view returns (bool) {
        address[] storage auditors = taskAuditors[taskId];
        for (uint i = 0; i < auditors.length; i++) {
            if (auditors[i] == auditor) return true;
        }
        return trustedAuditors[auditor];
    }

    function getTaskAuditors(bytes32 taskId) external view returns (address[] memory) {
        return taskAuditors[taskId];
    }

    // ============ 紧急函数 ============

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function emergencyWithdraw(address token, uint256 amount) external onlyOwner {
        if (token == address(0)) {
            payable(owner()).transfer(amount);
        } else {
            IERC20(token).safeTransfer(owner(), amount);
        }
    }

    // 允许合约接收 ETH
    receive() external payable {}
}

contracts/AutoPay.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title AutoPay
 * @dev 自动支付合约，管理用户对Agent的自动支付授权
 * @notice 支持 ERC20 代币的自动支付授权和执行
 */
contract AutoPay is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    // 支付代币
    IERC20 public paymentToken;
    // 授权结构
    struct Grant {
        address user;
        address agent;
        uint256 singleLimit; // 单次限额
        uint256 dailyLimit; // 每日限额
        uint256 usedToday; // 今日已用
        uint256 lastResetDate; // 上次重置日期
        uint256 expiresAt; // 过期时间
        bool isActive;
    }

    // 支付记录
    struct AutoPayment {
        bytes32 paymentId;
        address user;
        address agent;
        address recipient;
        uint256 amount;
        uint256 timestamp;
        bool executed;
    }

    // 事件
    event GrantCreated(
        address indexed user,
        address indexed agent,
        uint256 singleLimit,
        uint256 dailyLimit,
        uint256 expiresAt
    );

    event GrantRevoked(address indexed user, address indexed agent);

    event AutoPaymentExecuted(
        bytes32 indexed paymentId,
        address indexed user,
        address indexed agent,
        address recipient,
        uint256 amount
    );

    // 状态变量
    mapping(address => mapping(address => Grant)) public grants;
    mapping(bytes32 => AutoPayment) public autoPayments;

    /**
     * @dev 创建自动支付授权
     * @param agent Agent地址
     * @param singleLimit 单次限额
     * @param dailyLimit 每日限额
     * @param duration 授权时长（秒）
     */
    function createGrant(
        address agent,
        uint256 singleLimit,
        uint256 dailyLimit,
        uint256 duration
    ) external {
        require(agent != address(0), "Invalid agent address");
        require(singleLimit > 0 && dailyLimit > 0, "Invalid limits");
        require(singleLimit <= dailyLimit, "Single limit exceeds daily limit");

        grants[msg.sender][agent] = Grant({
            user: msg.sender,
            agent: agent,
            singleLimit: singleLimit,
            dailyLimit: dailyLimit,
            usedToday: 0,
            lastResetDate: block.timestamp / 1 days,
            expiresAt: block.timestamp + duration,
            isActive: true
        });

        emit GrantCreated(
            msg.sender,
            agent,
            singleLimit,
            dailyLimit,
            block.timestamp + duration
        );
    }

    /**
     * @dev 撤销授权
     * @param agent Agent地址
     */
    function revokeGrant(address agent) external {
        require(
            grants[msg.sender][agent].isActive,
            "Grant not found or inactive"
        );

        grants[msg.sender][agent].isActive = false;
        emit GrantRevoked(msg.sender, agent);
    }

    /**
     * @dev 执行自动支付（由Agent调用）
     * @param paymentId 支付ID
     * @param user 用户地址
     * @param recipient 收款地址
     * @param amount 支付金额
     */
    function executeAutoPayment(
        bytes32 paymentId,
        address user,
        address recipient,
        uint256 amount
    ) external nonReentrant whenNotPaused {
        require(address(paymentToken) != address(0), "Payment token not set");

        Grant storage grant = grants[user][msg.sender];
        require(grant.isActive, "Grant not active");
        require(block.timestamp <= grant.expiresAt, "Grant expired");
        require(amount <= grant.singleLimit, "Amount exceeds single limit");

        // 检查是否需要重置每日限额
        uint256 currentDate = block.timestamp / 1 days;
        if (currentDate > grant.lastResetDate) {
            grant.usedToday = 0;
            grant.lastResetDate = currentDate;
        }

        require(
            grant.usedToday + amount <= grant.dailyLimit,
            "Amount exceeds daily limit"
        );

        // 更新使用量
        grant.usedToday += amount;

        // 记录支付
        autoPayments[paymentId] = AutoPayment({
            paymentId: paymentId,
            user: user,
            agent: msg.sender,
            recipient: recipient,
            amount: amount,
            timestamp: block.timestamp,
            executed: true
        });

        // 执行转账 - 使用 SafeERC20 从用户钱包转账到收款人
        // 用户需要预先 approve 本合约
        paymentToken.safeTransferFrom(user, recipient, amount);

        emit AutoPaymentExecuted(paymentId, user, msg.sender, recipient, amount);
    }

    /**
     * @dev 查询授权信息
     * @param user 用户地址
     * @param agent Agent地址
     */
    function getGrant(address user, address agent)
        external
        view
        returns (Grant memory)
    {
        return grants[user][agent];
    }

    /**
     * @dev 查询支付记录
     * @param paymentId 支付ID
     */
    function getAutoPayment(bytes32 paymentId)
        external
        view
        returns (AutoPayment memory)
    {
        return autoPayments[paymentId];
    }

    // ============ 管理函数 ============

    /**
     * @dev 设置支付代币
     * @param _token 代币地址
     */
    function setPaymentToken(address _token) external onlyOwner {
        require(_token != address(0), "Invalid token address");
        paymentToken = IERC20(_token);
    }

    /**
     * @dev 暂停合约
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev 恢复合约
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev 紧急提款
     */
    function emergencyWithdraw(address token, address to, uint256 amount) external onlyOwner {
        require(to != address(0), "Invalid recipient");
        IERC20(token).safeTransfer(to, amount);
    }
}

contracts/Commission.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title Commission
 * @dev 分润结算合约，管理Agent和商户的分润记录与自动结算
 * @notice 支持多场景支付分账：QuickPay (X402)、钱包直接转账、Provider 法币转数字货币
 */
contract Commission is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;
    IERC20 public settlementToken;
    address public paymindTreasury;
    address public systemRebatePool;

    // 分润记录
    struct CommissionRecord {
        bytes32 recordId;
        address payee; // 收款方
        PayeeType payeeType;
        AgentType agentType; // Agent类型（仅当payeeType为AGENT时有效）
        uint256 amount;
        address currency; // 代币地址，address(0)表示ETH
        uint256 commissionBase; // 佣金计算基础（商户税前价格）
        uint256 channelFee; // 通道费用
        bytes32 sessionId; // Session ID（三层ID之一）
        uint256 timestamp;
        bool settled;
    }

    // 结算记录
    struct Settlement {
        bytes32 settlementId;
        address payee;
        PayeeType payeeType;
        uint256 totalAmount;
        address currency;
        uint256 settlementDate;
        bool completed;
    }

    enum PayeeType {
        AGENT,
        MERCHANT,
        PAYMIND
    }

    enum AgentType {
        EXECUTION,
        RECOMMENDATION,
        REFERRAL
    }

    enum Status {
        PENDING,
        PAID,
        PROCESSING,
        DELIVERED,
        SETTLED,
        FROZEN,
        REFUNDED
    }

    // 支付场景枚举
    enum PaymentScenario {
        QUICKPAY,      // QuickPay (X402)
        WALLET,        // 钱包直接转账
        PROVIDER_FIAT, // Provider 法币转数字货币
        PROVIDER_CRYPTO // Provider 数字货币转法币（提现）
    }

    struct Order {
        bytes32 orderId;
        address merchant;
        address referrer;
        address executor;
        uint256 merchantAmount;
        uint256 referralFee;
        uint256 executionFee;
        uint256 platformFee;
        uint256 settlementTime;
        bool executorHasWallet;
        bool isDisputed;
        Status status;
    }

    // 分账配置（链上存储）- 支持 Audit Proof
    struct SplitConfig {
        address merchantMPCWallet;  // 商户 MPC 钱包地址
        uint256 merchantAmount;     // 商户金额
        address referrer;            // 推荐人地址
        uint256 referralFee;         // 推荐人佣金
        address executor;            // 执行Agent地址
        uint256 executionFee;        // 执行Agent佣金
        uint256 platformFee;         // 平台费用
        uint256 offRampFee;          // PayMind Off-ramp分佣（可配置，默认0.1%，可为0）
        bool executorHasWallet;      // 执行Agent是否有钱包
        uint256 settlementTime;      // 结算时间（0表示即时结算）
        bool isDisputed;             // 是否有争议
        bytes32 sessionId;           // Session ID（三层ID之一）
        // Audit Proof 扩展字段
        bytes32 proofHash;           // 预期结果哈希（用于验证任务完成）
        address auditor;             // 审计员地址（验证任务完成的签名者）
        bool requiresProof;          // 是否需要 Proof 才能释放资金
        bool proofVerified;          // Proof 是否已验证
    }

    // 事件
    event CommissionRecorded(
        bytes32 indexed recordId,
        address indexed payee,
        PayeeType payeeType,
        AgentType agentType,
        uint256 amount,
        address currency,
        uint256 commissionBase,
        uint256 channelFee,
        bytes32 sessionId
    );

    event SettlementCreated(
        bytes32 indexed settlementId,
        address indexed payee,
        PayeeType payeeType,
        uint256 totalAmount,
        address currency
    );

    event SettlementCompleted(
        bytes32 indexed settlementId,
        address indexed payee,
        uint256 amount,
        address currency
    );

    event OrderSynced(bytes32 indexed orderId, Status status, uint256 settlementTime);
    event CommissionDistributed(bytes32 indexed orderId, uint256 merchantAmount);

    // 新的事件
    event PaymentReceived(
        bytes32 indexed orderId,
        PaymentScenario scenario,
        address indexed from,
        uint256 amount
    );

    event PaymentAutoSplit(
        bytes32 indexed orderId,
        bytes32 indexed sessionId,
        address indexed merchantWallet,
        uint256 totalAmount,
        uint256 merchantAmount,
        uint256 platformFee,
        uint256 executionFee,
        uint256 referralFee
    );

    event SplitConfigSet(bytes32 indexed orderId, SplitConfig config);
    event ProviderAuthorized(address indexed provider, bool authorized);

    // Audit Proof 事件
    event ProofSubmitted(bytes32 indexed orderId, bytes32 resultHash, address indexed submitter);
    event ProofVerified(bytes32 indexed orderId, bytes32 resultHash, address indexed auditor, uint256 quality);
    event ProofRejected(bytes32 indexed orderId, string reason, address indexed auditor);
    event TrustedAuditorUpdated(address indexed auditor, bool trusted);

    // 状态变量
    mapping(bytes32 => CommissionRecord) public commissions;
    mapping(bytes32 => Settlement) public settlements;
    mapping(address => mapping(address => uint256)) public pendingBalances; // payee => currency => amount
    mapping(bytes32 => Order) public orders;

    // 新状态变量
    mapping(bytes32 => SplitConfig) public orderSplitConfigs; // 订单分账配置
    mapping(address => bool) public authorizedProviders; // Provider 白名单
    mapping(address => bool) public relayers; // Relayer 白名单

    // Audit Proof 状态变量
    mapping(address => bool) public trustedAuditors; // 受信任的审计员
    mapping(bytes32 => bytes32) public submittedProofs; // orderId => submittedResultHash

    modifier onlyRelayer() {
        require(relayers[msg.sender] || msg.sender == owner(), "Not relayer");
        _;
    }

    modifier onlyAuditor(bytes32 orderId) {
        SplitConfig storage config = orderSplitConfigs[orderId];
        require(
            trustedAuditors[msg.sender] || 
            config.auditor == msg.sender || 
            msg.sender == owner(),
            "Not authorized auditor"
        );
        _;
    }

    function setRelayer(address relayer, bool active) external onlyOwner {
        relayers[relayer] = active;
    }

    function configureSettlementToken(
        address token,
        address treasury,
        address rebatePool
    ) external onlyOwner {
        require(token != address(0) && treasury != address(0), "Invalid addresses");
        settlementToken = IERC20(token);
        paymindTreasury = treasury;
        systemRebatePool = rebatePool;
    }

    function updateSystemRebatePool(address rebatePool) external onlyOwner {
        systemRebatePool = rebatePool;
    }

    function syncOrder(
        bytes32 orderId,
        address merchant,
        address referrer,
        address executor,
        uint256 merchantAmount,
        uint256 referralFee,
        uint256 executionFee,
        uint256 platformFee,
        uint256 settlementTime,
        bool executorHasWallet,
        bool isDisputed,
        Status status
    ) external onlyOwner {
        require(orderId != bytes32(0), "Invalid order");
        Order storage o = orders[orderId];
        o.orderId = orderId;
        o.merchant = merchant;
        o.referrer = referrer;
        o.executor = executor;
        o.merchantAmount = merchantAmount;
        o.referralFee = referralFee;
        o.executionFee = executionFee;
        o.platformFee = platformFee;
        o.settlementTime = settlementTime;
        o.executorHasWallet = executorHasWallet;
        o.isDisputed = isDisputed;
        o.status = status;

        emit OrderSynced(orderId, status, settlementTime);
    }

    function setOrderDispute(bytes32 orderId, bool disputed) external onlyOwner {
        Order storage o = orders[orderId];
        require(o.orderId != bytes32(0), "Order not found");
        o.isDisputed = disputed;
        o.status = disputed ? Status.FROZEN : o.status;
    }

    /**
     * @dev 兼容现有的 distributeCommission 函数
     * 支持两种模式：
     * 1. 使用新的 SplitConfig（如果已设置）
     * 2. 使用旧的 Order 结构（向后兼容）
     * 
     * 如果 SplitConfig.requiresProof = true，则必须先验证 proof
     */
    function distributeCommission(bytes32 orderId) external nonReentrant {
        // 优先使用新的 SplitConfig
        SplitConfig storage config = orderSplitConfigs[orderId];
        if (config.merchantMPCWallet != address(0)) {
            // 检查是否需要 Proof 验证
            if (config.requiresProof) {
                require(config.proofVerified, "Proof not verified");
            }

            // 使用新的分账配置
            uint256 contractBalance = settlementToken.balanceOf(address(this));
            require(contractBalance > 0, "No balance to distribute");

            uint256 totalAmount = config.merchantAmount + 
                                 config.referralFee + 
                                 config.executionFee + 
                                 config.platformFee;

            if (totalAmount == 0) {
                totalAmount = contractBalance;
            }

            _autoSplit(orderId, totalAmount);
            return;
        }

        // 向后兼容：使用旧的 Order 结构
        Order storage o = orders[orderId];
        require(o.orderId != bytes32(0), "Order not found");
        require(o.status == Status.DELIVERED, "Order not ready");
        require(!o.isDisputed, "Order is disputed");
        require(block.timestamp >= o.settlementTime, "Still in locking period");
        require(address(settlementToken) != address(0), "Token not configured");
        require(paymindTreasury != address(0), "Treasury not configured");

        if (o.merchant != address(0) && o.merchantAmount > 0) {
            settlementToken.safeTransfer(o.merchant, o.merchantAmount);
        }

        if (o.referralFee > 0 && o.referrer != address(0)) {
            settlementToken.safeTransfer(o.referrer, o.referralFee);
        }

        if (o.executionFee > 0) {
            address target = o.executorHasWallet ? o.executor : systemRebatePool;
            if (target == address(0)) {
                target = paymindTreasury;
            }
            settlementToken.safeTransfer(target, o.executionFee);
        }

        if (o.platformFee > 0) {
            settlementToken.safeTransfer(paymindTreasury, o.platformFee);
        }

        o.status = Status.SETTLED;
        emit CommissionDistributed(orderId, o.merchantAmount);
    }

    /**
     * @dev 记录分润（新版本，支持多Agent协作和Session ID）
     * @param payee 收款方地址
     * @param payeeType 收款方类型
     * @param agentType Agent类型（仅当payeeType为AGENT时有效）
     * @param amount 分润金额
     * @param currency 代币地址
     * @param commissionBase 佣金计算基础（商户税前价格）
     * @param channelFee 通道费用
     * @param sessionId Session ID（三层ID之一）
     */
    function recordCommission(
        address payee,
        PayeeType payeeType,
        AgentType agentType,
        uint256 amount,
        address currency,
        uint256 commissionBase,
        uint256 channelFee,
        bytes32 sessionId
    ) external onlyOwner {
        require(payee != address(0), "Invalid payee address");
        require(amount > 0, "Amount must be greater than 0");

        bytes32 recordId = keccak256(
            abi.encodePacked(
                payee,
                payeeType,
                agentType,
                amount,
                currency,
                commissionBase,
                channelFee,
                sessionId,
                block.timestamp
            )
        );

        commissions[recordId] = CommissionRecord({
            recordId: recordId,
            payee: payee,
            payeeType: payeeType,
            agentType: agentType,
            amount: amount,
            currency: currency,
            commissionBase: commissionBase,
            channelFee: channelFee,
            sessionId: sessionId,
            timestamp: block.timestamp,
            settled: false
        });

        pendingBalances[payee][currency] += amount;

        emit CommissionRecorded(
            recordId,
            payee,
            payeeType,
            agentType,
            amount,
            currency,
            commissionBase,
            channelFee,
            sessionId
        );
    }

    /**
     * @dev 记录分润（旧版本，保持向后兼容）
     * @param payee 收款方地址
     * @param payeeType 收款方类型
     * @param amount 分润金额
     * @param currency 代币地址
     */
    function recordCommissionLegacy(
        address payee,
        PayeeType payeeType,
        uint256 amount,
        address currency
    ) external onlyOwner {
        require(payee != address(0), "Invalid payee address");
        require(amount > 0, "Amount must be greater than 0");

        bytes32 recordId = keccak256(
            abi.encodePacked(
                payee,
                payeeType,
                amount,
                currency,
                block.timestamp
            )
        );

        commissions[recordId] = CommissionRecord({
            recordId: recordId,
            payee: payee,
            payeeType: payeeType,
            agentType: AgentType.EXECUTION, // 默认执行Agent
            amount: amount,
            currency: currency,
            commissionBase: amount, // 默认使用amount作为基础
            channelFee: 0,
            sessionId: bytes32(0),
            timestamp: block.timestamp,
            settled: false
        });

        pendingBalances[payee][currency] += amount;

        emit CommissionRecorded(
            recordId,
            payee,
            payeeType,
            AgentType.EXECUTION,
            amount,
            currency,
            amount,
            0,
            bytes32(0)
        );
    }

    /**
     * @dev 创建结算
     * @param payee 收款方地址
     * @param payeeType 收款方类型
     * @param currency 代币地址
     */
    function createSettlement(
        address payee,
        PayeeType payeeType,
        address currency
    ) external onlyOwner returns (bytes32) {
        uint256 totalAmount = pendingBalances[payee][currency];
        require(totalAmount > 0, "No pending balance");

        bytes32 settlementId = keccak256(
            abi.encodePacked(
                payee,
                payeeType,
                currency,
                block.timestamp
            )
        );

        settlements[settlementId] = Settlement({
            settlementId: settlementId,
            payee: payee,
            payeeType: payeeType,
            totalAmount: totalAmount,
            currency: currency,
            settlementDate: block.timestamp,
            completed: false
        });

        emit SettlementCreated(
            settlementId,
            payee,
            payeeType,
            totalAmount,
            currency
        );

        return settlementId;
    }

    /**
     * @dev 执行结算
     * @param settlementId 结算ID
     */
    function executeSettlement(bytes32 settlementId) external nonReentrant {
        Settlement storage settlement = settlements[settlementId];
        require(settlement.settlementId != bytes32(0), "Settlement not found");
        require(!settlement.completed, "Settlement already completed");

        uint256 amount = settlement.totalAmount;
        address currency = settlement.currency;

        // 清零待结算余额
        pendingBalances[settlement.payee][currency] = 0;

        // 执行转账
        if (currency == address(0)) {
            // ETH转账 - 使用 call 替代 transfer 以支持更高 gas 限制
            (bool success, ) = payable(settlement.payee).call{value: amount}("");
            require(success, "ETH transfer failed");
        } else {
            // ERC20代币转账 - 使用 SafeERC20
            IERC20 token = IERC20(currency);
            require(
                token.balanceOf(address(this)) >= amount,
                "Insufficient token balance"
            );
            token.safeTransfer(settlement.payee, amount);
        }

        settlement.completed = true;

        emit SettlementCompleted(
            settlementId,
            settlement.payee,
            amount,
            currency
        );
    }

    /**
     * @dev 查询待结算余额
     * @param payee 收款方地址
     * @param currency 代币地址
     */
    function getPendingBalance(address payee, address currency)
        external
        view
        returns (uint256)
    {
        return pendingBalances[payee][currency];
    }

    // ============ 统一分账函数 ============

    /**
     * @dev 统一的分账函数（内部函数）
     * @param orderId 订单ID
     * @param totalAmount 总金额
     */
    function _autoSplit(bytes32 orderId, uint256 totalAmount) internal {
        SplitConfig storage config = orderSplitConfigs[orderId];
        require(config.merchantMPCWallet != address(0), "Order config not found");
        require(!config.isDisputed, "Order is disputed");

        // 验证结算时间（如果需要）
        if (config.settlementTime > 0) {
            require(
                block.timestamp >= config.settlementTime,
                "Settlement time not reached"
            );
        }

        // 验证 USDC 余额
        require(
            settlementToken.balanceOf(address(this)) >= totalAmount,
            "Insufficient USDC balance"
        );

        // 记录已分配金额，用于防止精度损失
        uint256 distributed = 0;

        // 分账到商户 MPC 钱包
        if (config.merchantMPCWallet != address(0) && config.merchantAmount > 0) {
            settlementToken.safeTransfer(config.merchantMPCWallet, config.merchantAmount);
            distributed += config.merchantAmount;
        }

        // 分账到推荐人
        if (config.referralFee > 0 && config.referrer != address(0)) {
            settlementToken.safeTransfer(config.referrer, config.referralFee);
            distributed += config.referralFee;
        }

        // 分账到执行Agent
        if (config.executionFee > 0) {
            address target = config.executorHasWallet 
                ? config.executor 
                : systemRebatePool;
            if (target == address(0)) {
                target = paymindTreasury;
            }
            settlementToken.safeTransfer(target, config.executionFee);
            distributed += config.executionFee;
        }

        // 分账到平台
        if (config.platformFee > 0) {
            settlementToken.safeTransfer(paymindTreasury, config.platformFee);
            distributed += config.platformFee;
        }

        // 分账到PayMind Treasury（Off-ramp分佣，可配置，可为0）
        if (config.offRampFee > 0) {
            settlementToken.safeTransfer(paymindTreasury, config.offRampFee);
            distributed += config.offRampFee;
        }

        // 处理精度损失：将剩余金额转给平台
        if (totalAmount > distributed) {
            uint256 remaining = totalAmount - distributed;
            settlementToken.safeTransfer(paymindTreasury, remaining);
        }

        // 记录分账事件
        emit PaymentAutoSplit(
            orderId,
            config.sessionId,
            config.merchantMPCWallet,
            totalAmount,
            config.merchantAmount,
            config.platformFee,
            config.executionFee,
            config.referralFee
        );
    }

    // ============ 场景 1: QuickPay (X402) ============

    /**
     * @dev QuickPay 支付（X402 Session）
     * 注意：Session 签名验证应该在调用此函数之前完成（由 ERC8004 合约或 Relayer 验证）
     * @param orderId 订单ID
     * @param amount 支付金额
     */
    function quickPaySplit(
        bytes32 orderId,
        uint256 amount
    ) external nonReentrant whenNotPaused {
        // 1. 从用户钱包转账 USDC 到合约 - 使用 SafeERC20
        settlementToken.safeTransferFrom(msg.sender, address(this), amount);

        // 2. 记录支付
        emit PaymentReceived(orderId, PaymentScenario.QUICKPAY, msg.sender, amount);

        // 3. 自动分账
        _autoSplit(orderId, amount);
    }

    /**
     * @dev QuickPay 支付（通过 Relayer/SessionManager）
     * 允许 Relayer 指定付款人（前提是 Relayer 已验证 Session）
     * @param orderId 订单ID
     * @param amount 支付金额
     * @param payer 付款人地址
     */
    function quickPaySplitFrom(
        bytes32 orderId,
        uint256 amount,
        address payer
    ) external nonReentrant onlyRelayer whenNotPaused {
        // 1. 从指定付款人钱包转账 USDC 到合约 - 使用 SafeERC20
        // 注意：付款人必须已授权 Commission 合约（或通过 Permit）
        settlementToken.safeTransferFrom(payer, address(this), amount);

        // 2. 记录支付
        emit PaymentReceived(orderId, PaymentScenario.QUICKPAY, payer, amount);

        // 3. 自动分账
        _autoSplit(orderId, amount);
    }

    // ============ 场景 2: 钱包直接转账 ============

    /**
     * @dev 钱包直接转账分账
     * @param orderId 订单ID
     * @param amount 支付金额
     */
    function walletSplit(
        bytes32 orderId,
        uint256 amount
    ) external nonReentrant whenNotPaused {
        // 1. 从用户钱包转账 USDC 到合约 - 使用 SafeERC20
        settlementToken.safeTransferFrom(msg.sender, address(this), amount);

        // 2. 记录支付
        emit PaymentReceived(orderId, PaymentScenario.WALLET, msg.sender, amount);

        // 3. 自动分账
        _autoSplit(orderId, amount);
    }

    // ============ 场景 3: Provider 法币转数字货币 ============

    /**
     * @dev Provider 发送 USDC 到合约并自动分账
     * @param orderId 订单ID
     * @param amount 支付金额
     */
    function providerFiatToCryptoSplit(
        bytes32 orderId,
        uint256 amount
    ) external nonReentrant whenNotPaused {
        // 1. 验证 Provider 地址（白名单）
        require(authorizedProviders[msg.sender], "Unauthorized provider");

        // 2. 从 Provider 钱包转账 USDC 到合约 - 使用 SafeERC20
        // 验证转账前后余额变化，防止转账金额不匹配
        uint256 balanceBefore = settlementToken.balanceOf(address(this));
        settlementToken.safeTransferFrom(msg.sender, address(this), amount);
        uint256 balanceAfter = settlementToken.balanceOf(address(this));
        require(balanceAfter - balanceBefore >= amount, "Transfer amount mismatch");

        // 3. 记录支付
        emit PaymentReceived(orderId, PaymentScenario.PROVIDER_FIAT, msg.sender, amount);

        // 4. 自动分账
        _autoSplit(orderId, amount);
    }

    // ============ 配置函数 ============

    /**
     * @dev 设置订单分账配置
     * @param orderId 订单ID
     * @param config 分账配置
     */
    function setSplitConfig(
        bytes32 orderId,
        SplitConfig memory config
    ) external {
        require(msg.sender == owner() || relayers[msg.sender], "Unauthorized");
        require(config.merchantMPCWallet != address(0), "Invalid merchant wallet");
        orderSplitConfigs[orderId] = config;
        emit SplitConfigSet(orderId, config);
    }

    /**
     * @dev 设置 Provider 白名单（可选）
     */
    function setAuthorizedProvider(address provider, bool authorized) external onlyOwner {
        authorizedProviders[provider] = authorized;
        emit ProviderAuthorized(provider, authorized);
    }

    /**
     * @dev 设置争议状态
     */
    function setDisputeStatus(bytes32 orderId, bool isDisputed) external onlyOwner {
        SplitConfig storage config = orderSplitConfigs[orderId];
        require(config.merchantMPCWallet != address(0), "Order not found");
        config.isDisputed = isDisputed;
    }

    // ============ 查询函数 ============

    /**
     * @dev 查询订单分账配置
     */
    function getSplitConfig(bytes32 orderId) 
        external 
        view 
        returns (SplitConfig memory) 
    {
        return orderSplitConfigs[orderId];
    }

    /**
     * @dev 查询合约 USDC 余额
     */
    function getContractBalance() external view returns (uint256) {
        return settlementToken.balanceOf(address(this));
    }

    // ============ 紧急控制函数 ============

    /**
     * @dev 暂停合约
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev 恢复合约
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev 紧急提款 ERC20 代币
     * @param token 代币地址
     * @param to 接收地址
     * @param amount 金额
     */
    function emergencyWithdrawToken(
        address token,
        address to,
        uint256 amount
    ) external onlyOwner {
        require(to != address(0), "Invalid recipient");
        IERC20(token).safeTransfer(to, amount);
    }

    /**
     * @dev 紧急提款 ETH
     * @param to 接收地址
     * @param amount 金额
     */
    function emergencyWithdrawETH(
        address to,
        uint256 amount
    ) external onlyOwner {
        require(to != address(0), "Invalid recipient");
        require(address(this).balance >= amount, "Insufficient balance");
        (bool success, ) = payable(to).call{value: amount}("");
        require(success, "ETH transfer failed");
    }

    // ============ Audit Proof 函数 ============

    /**
     * @dev 设置受信任的审计员
     */
    function setTrustedAuditor(address auditor, bool trusted) external onlyOwner {
        trustedAuditors[auditor] = trusted;
        emit TrustedAuditorUpdated(auditor, trusted);
    }

    /**
     * @dev 设置订单的 Proof 要求
     * @param orderId 订单 ID
     * @param proofHash 预期结果哈希
     * @param auditor 指定审计员（可选，address(0) 表示使用全局审计员）
     */
    function setProofRequirement(
        bytes32 orderId,
        bytes32 proofHash,
        address auditor
    ) external onlyRelayer {
        SplitConfig storage config = orderSplitConfigs[orderId];
        require(config.merchantMPCWallet != address(0), "Config not found");

        config.proofHash = proofHash;
        config.auditor = auditor;
        config.requiresProof = true;
        config.proofVerified = false;
    }

    /**
     * @dev 执行者提交任务结果哈希
     * @param orderId 订单 ID
     * @param resultHash 结果哈希（如图片哈希）
     */
    function submitProof(bytes32 orderId, bytes32 resultHash) external {
        SplitConfig storage config = orderSplitConfigs[orderId];
        require(config.requiresProof, "Proof not required");
        require(!config.proofVerified, "Already verified");
        require(
            msg.sender == config.executor || msg.sender == config.merchantMPCWallet,
            "Not authorized"
        );

        submittedProofs[orderId] = resultHash;
        emit ProofSubmitted(orderId, resultHash, msg.sender);

        // 如果预期哈希匹配，自动验证
        if (config.proofHash != bytes32(0) && resultHash == config.proofHash) {
            config.proofVerified = true;
            emit ProofVerified(orderId, resultHash, address(this), 100);
        }
    }

    /**
     * @dev 审计员验证 Proof
     * @param orderId 订单 ID
     * @param approved 是否通过
     * @param quality 质量评分 (0-100)
     */
    function verifyProof(
        bytes32 orderId,
        bool approved,
        uint256 quality
    ) external onlyAuditor(orderId) {
        SplitConfig storage config = orderSplitConfigs[orderId];
        require(config.requiresProof, "Proof not required");
        require(!config.proofVerified, "Already verified");
        require(submittedProofs[orderId] != bytes32(0), "No proof submitted");

        if (approved && quality >= 60) {
            config.proofVerified = true;
            emit ProofVerified(orderId, submittedProofs[orderId], msg.sender, quality);
        } else {
            emit ProofRejected(orderId, "Quality insufficient or rejected", msg.sender);
        }
    }

    /**
     * @dev 查询订单的 Proof 状态
     */
    function getProofStatus(bytes32 orderId) external view returns (
        bool requiresProof,
        bytes32 expectedHash,
        bytes32 submittedHash,
        bool verified,
        address auditor
    ) {
        SplitConfig storage config = orderSplitConfigs[orderId];
        return (
            config.requiresProof,
            config.proofHash,
            submittedProofs[orderId],
            config.proofVerified,
            config.auditor
        );
    }

    /**
     * @dev 接收ETH
     */
    receive() external payable {}
}

contracts/ERC8004SessionManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

/**
 * @title ERC8004SessionManager
 * @dev ERC-8004 标准实现：Session Key 管理合约
 * 支持 Agent 自动化支付、高频小额支付场景
 */
contract ERC8004SessionManager is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    struct Session {
        address signer;           // Session Key 地址
        address owner;            // 主钱包地址
        uint256 singleLimit;      // 单笔限额（USDC，6 decimals）
        uint256 dailyLimit;       // 每日限额（USDC，6 decimals）
        uint256 usedToday;        // 今日已用（USDC，6 decimals）
        uint256 expiry;           // 过期时间戳
        uint256 lastResetDate;    // 上次重置日期（用于每日限额重置）
        bool isActive;           // 是否激活
    }

    // 状态变量
    mapping(bytes32 => Session) public sessions;
    mapping(address => bytes32[]) public userSessions; // 用户的所有 Session
    address public usdcToken; // USDC 代币地址
    address public relayer;   // Relayer 地址

    // 事件
    event SessionCreated(
        bytes32 indexed sessionId,
        address indexed owner,
        address indexed signer,
        uint256 singleLimit,
        uint256 dailyLimit,
        uint256 expiry
    );

    event PaymentExecuted(
        bytes32 indexed sessionId,
        address indexed to,
        uint256 amount,
        bytes32 indexed paymentId
    );

    event SessionRevoked(
        bytes32 indexed sessionId,
        address indexed owner
    );

    event DailyLimitReset(
        bytes32 indexed sessionId,
        uint256 newDate
    );

    // 修饰符
    modifier onlyRelayer() {
        require(msg.sender == relayer, "Only relayer can execute");
        _;
    }

    modifier validSession(bytes32 sessionId) {
        Session storage session = sessions[sessionId];
        require(session.owner != address(0), "Session not found");
        require(session.isActive, "Session not active");
        require(block.timestamp <= session.expiry, "Session expired");
        _;
    }

    constructor(address _usdcToken) {
        require(_usdcToken != address(0), "Invalid USDC address");
        usdcToken = _usdcToken;
    }

    /**
     * @dev 创建 Session
     * @param signer Session Key 地址
     * @param singleLimit 单笔限额（USDC，6 decimals）
     * @param dailyLimit 每日限额（USDC，6 decimals）
     * @param expiry 过期时间戳
     */
    function createSession(
        address signer,
        uint256 singleLimit,
        uint256 dailyLimit,
        uint256 expiry
    ) external returns (bytes32 sessionId) {
        require(signer != address(0), "Invalid signer");
        require(singleLimit > 0, "Invalid single limit");
        require(dailyLimit >= singleLimit, "Daily limit must >= single limit");
        require(expiry > block.timestamp, "Invalid expiry");

        // 生成 Session ID
        sessionId = keccak256(
            abi.encodePacked(
                msg.sender,
                signer,
                block.timestamp,
                block.number,
                blockhash(block.number - 1)
            )
        );

        // 确保 Session ID 唯一
        require(sessions[sessionId].owner == address(0), "Session ID collision");

        // 创建 Session
        sessions[sessionId] = Session({
            signer: signer,
            owner: msg.sender,
            singleLimit: singleLimit,
            dailyLimit: dailyLimit,
            usedToday: 0,
            expiry: expiry,
            lastResetDate: block.timestamp / 1 days,
            isActive: true
        });

        // 记录用户 Session
        userSessions[msg.sender].push(sessionId);

        emit SessionCreated(
            sessionId,
            msg.sender,
            signer,
            singleLimit,
            dailyLimit,
            expiry
        );

        return sessionId;
    }

    /**
     * @dev 使用 Session 执行支付（由 Relayer 调用）
     * @param sessionId Session ID
     * @param to 收款地址
     * @param amount 支付金额（USDC，6 decimals）
     * @param paymentId 支付 ID（用于追踪）
     * @param signature Session Key 签名
     */
    function executeWithSession(
        bytes32 sessionId,
        address to,
        uint256 amount,
        bytes32 paymentId,
        bytes calldata signature
    ) external onlyRelayer validSession(sessionId) nonReentrant whenNotPaused {
        _executeWithSession(sessionId, to, amount, paymentId, signature);
    }

    /**
     * @dev 内部执行支付逻辑
     */
    function _executeWithSession(
        bytes32 sessionId,
        address to,
        uint256 amount,
        bytes32 paymentId,
        bytes calldata signature
    ) internal {
        Session storage session = sessions[sessionId];

        // 检查每日限额重置
        uint256 currentDate = block.timestamp / 1 days;
        if (currentDate > session.lastResetDate) {
            session.usedToday = 0;
            session.lastResetDate = currentDate;
            emit DailyLimitReset(sessionId, currentDate);
        }

        // 检查单笔限额
        require(amount <= session.singleLimit, "Exceeds single limit");

        // 检查每日限额
        require(
            session.usedToday + amount <= session.dailyLimit,
            "Exceeds daily limit"
        );

        // 验证签名（使用 EIP-712 兼容的消息哈希）
        bytes32 messageHash = keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(
                    abi.encodePacked(
                        sessionId,
                        to,
                        amount,
                        paymentId,
                        block.chainid
                    )
                )
            )
        );
        address recoveredSigner = recoverSigner(messageHash, signature);
        require(recoveredSigner == session.signer, "Invalid signature");

        // 更新已用额度
        session.usedToday += amount;

        // 从用户钱包转账 USDC 到收款地址
        // ⚠️ 重要：amount参数是6 decimals（用于签名验证和限额检查）
        // 但代币的实际精度可能是18 decimals（USDT），需要转换
        // 获取代币的实际精度
        uint8 tokenDecimals = IERC20Metadata(usdcToken).decimals();
        uint256 amountForTransfer = amount;
        
        // 如果代币精度不是6 decimals，需要转换
        if (tokenDecimals != 6) {
            if (tokenDecimals > 6) {
                // 从6 decimals转换为更高精度（例如：6 -> 18，乘以 10^12）
                uint256 scaleFactor = 10 ** (tokenDecimals - 6);
                amountForTransfer = amount * scaleFactor;
            } else {
                // 从6 decimals转换为更低精度（例如：6 -> 3，除以 10^3）
                uint256 scaleFactor = 10 ** (6 - tokenDecimals);
                amountForTransfer = amount / scaleFactor;
            }
        }
        
        IERC20(usdcToken).safeTransferFrom(
            session.owner,
            to,
            amountForTransfer
        );

        emit PaymentExecuted(sessionId, to, amount, paymentId);
    }

    /**
     * @dev 批量执行支付（节省 Gas）
     * @param sessionIds Session ID 数组
     * @param recipients 收款地址数组
     * @param amounts 支付金额数组
     * @param paymentIds 支付 ID 数组
     * @param signatures 签名数组
     */
    function executeBatchWithSession(
        bytes32[] calldata sessionIds,
        address[] calldata recipients,
        uint256[] calldata amounts,
        bytes32[] calldata paymentIds,
        bytes[] calldata signatures
    ) external onlyRelayer nonReentrant whenNotPaused {
        require(
            sessionIds.length == recipients.length &&
            recipients.length == amounts.length &&
            amounts.length == paymentIds.length &&
            paymentIds.length == signatures.length,
            "Array length mismatch"
        );
        require(sessionIds.length > 0 && sessionIds.length <= 50, "Invalid batch size");

        for (uint256 i = 0; i < sessionIds.length; i++) {
            // 首先验证 Session 有效性
            bytes32 sid = sessionIds[i];
            Session storage session = sessions[sid];
            require(session.owner != address(0), "Session not found");
            require(session.isActive, "Session not active");
            require(block.timestamp <= session.expiry, "Session expired");

            _executeWithSession(
                sid,
                recipients[i],
                amounts[i],
                paymentIds[i],
                signatures[i]
            );
        }
    }

    /**
     * @dev 撤销 Session
     * @param sessionId Session ID
     */
    function revokeSession(bytes32 sessionId) external {
        Session storage session = sessions[sessionId];
        require(session.owner == msg.sender, "Not session owner");
        require(session.isActive, "Session already revoked");

        session.isActive = false;

        emit SessionRevoked(sessionId, msg.sender);
    }

    /**
     * @dev 获取 Session 信息
     * @param sessionId Session ID
     */
    function getSession(bytes32 sessionId)
        external
        view
        returns (Session memory)
    {
        return sessions[sessionId];
    }

    /**
     * @dev 获取用户的所有 Session
     * @param user 用户地址
     */
    function getUserSessions(address user)
        external
        view
        returns (bytes32[] memory)
    {
        return userSessions[user];
    }

    /**
     * @dev 查询用户的代币授权状态
     * @param user 用户地址
     * @return allowance 授权额度
     * @return tokenAddress 代币地址
     * @return isAuthorized 是否已授权
     */
    function getUserTokenAllowance(address user)
        external
        view
        returns (
            uint256 allowance,
            address tokenAddress,
            bool isAuthorized
        )
    {
        tokenAddress = usdcToken;
        allowance = IERC20(usdcToken).allowance(user, address(this));
        isAuthorized = allowance > 0;
    }

    /**
     * @dev 设置 Relayer 地址
     */
    function setRelayer(address _relayer) external onlyOwner {
        require(_relayer != address(0), "Invalid relayer");
        relayer = _relayer;
    }

    /**
     * @dev 恢复签名者地址
     */
    function recoverSigner(bytes32 messageHash, bytes memory signature)
        internal
        pure
        returns (address)
    {
        require(signature.length == 65, "Invalid signature length");

        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            r := mload(add(signature, 32))
            s := mload(add(signature, 64))
            v := byte(0, mload(add(signature, 96)))
        }

        if (v < 27) {
            v += 27;
        }

        require(v == 27 || v == 28, "Invalid signature");

        return ecrecover(messageHash, v, r, s);
    }

    // ============ 紧急控制函数 ============

    /**
     * @dev 暂停合约
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev 恢复合约
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev 紧急提款
     */
    function emergencyWithdraw(address token, address to, uint256 amount) external onlyOwner {
        require(to != address(0), "Invalid recipient");
        IERC20(token).safeTransfer(to, amount);
    }
}

