// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title PaymentRouter
 * @dev 支付路由合约，支持多种支付方式的路由选择
 */
contract PaymentRouter is Ownable, ReentrancyGuard, Pausable {
    // 支付方式枚举
    enum PaymentMethod {
        STRIPE,
        WALLET,
        X402,
        MULTISIG
    }

    // 支付通道结构
    struct PaymentChannel {
        address channelAddress;
        bool isActive;
        uint256 priority; // 优先级，数字越大优先级越高
        uint256 minAmount; // 最小金额
        uint256 maxAmount; // 最大金额
    }

    // 支付记录
    struct PaymentRecord {
        bytes32 paymentId;
        address payer;
        address recipient;
        uint256 amount;
        PaymentMethod method;
        bytes32 sessionId; // Session ID（三层ID之一）
        uint256 merchantPrice; // 商户设置的价格（根据国家、通道）
        uint256 channelFee; // 通道费用
        bool completed;
        uint256 timestamp;
    }

    // 事件
    event PaymentRouted(
        bytes32 indexed paymentId,
        address indexed payer,
        address indexed recipient,
        uint256 amount,
        PaymentMethod method,
        bytes32 sessionId,
        uint256 merchantPrice,
        uint256 channelFee
    );

    event PaymentCompleted(
        bytes32 indexed paymentId,
        address indexed recipient,
        uint256 amount
    );

    // 状态变量
    mapping(PaymentMethod => PaymentChannel) public paymentChannels;
    mapping(bytes32 => PaymentRecord) public payments;
    mapping(address => uint256) public balances;

    /**
     * @dev 路由支付请求（新版本，支持Session ID和商户价格）
     * @param paymentId 支付ID
     * @param recipient 收款地址
     * @param amount 支付金额
     * @param method 支付方式
     * @param sessionId Session ID（三层ID之一）
     * @param merchantPrice 商户设置的价格（根据国家、通道）
     * @param channelFee 通道费用
     */
    function routePayment(
        bytes32 paymentId,
        address recipient,
        uint256 amount,
        PaymentMethod method,
        bytes32 sessionId,
        uint256 merchantPrice,
        uint256 channelFee
    ) external payable nonReentrant whenNotPaused {
        require(recipient != address(0), "Invalid recipient");
        require(amount > 0, "Amount must be greater than 0");
        require(
            paymentChannels[method].isActive,
            "Payment channel not active"
        );
        require(
            amount >= paymentChannels[method].minAmount &&
                amount <= paymentChannels[method].maxAmount,
            "Amount out of range"
        );

        // 创建支付记录
        payments[paymentId] = PaymentRecord({
            paymentId: paymentId,
            payer: msg.sender,
            recipient: recipient,
            amount: amount,
            method: method,
            sessionId: sessionId,
            merchantPrice: merchantPrice,
            channelFee: channelFee,
            completed: false,
            timestamp: block.timestamp
        });

        emit PaymentRouted(
            paymentId,
            msg.sender,
            recipient,
            amount,
            method,
            sessionId,
            merchantPrice,
            channelFee
        );

        // 根据支付方式处理
        if (method == PaymentMethod.WALLET) {
            // 直接转账
            require(msg.value >= amount, "Insufficient payment");
            balances[recipient] += amount;
            payments[paymentId].completed = true;
            emit PaymentCompleted(paymentId, recipient, amount);
        }
        // X402和MULTISIG需要其他合约处理
    }

    /**
     * @dev 路由支付请求（旧版本，保持向后兼容）
     * @param paymentId 支付ID
     * @param recipient 收款地址
     * @param amount 支付金额
     * @param method 支付方式
     */
    function routePaymentLegacy(
        bytes32 paymentId,
        address recipient,
        uint256 amount,
        PaymentMethod method
    ) external payable nonReentrant whenNotPaused {
        require(recipient != address(0), "Invalid recipient");
        require(amount > 0, "Amount must be greater than 0");
        require(
            paymentChannels[method].isActive,
            "Payment channel not active"
        );
        require(
            amount >= paymentChannels[method].minAmount &&
                amount <= paymentChannels[method].maxAmount,
            "Amount out of range"
        );

        // 创建支付记录
        payments[paymentId] = PaymentRecord({
            paymentId: paymentId,
            payer: msg.sender,
            recipient: recipient,
            amount: amount,
            method: method,
            sessionId: bytes32(0),
            merchantPrice: amount,
            channelFee: 0,
            completed: false,
            timestamp: block.timestamp
        });

        emit PaymentRouted(
            paymentId,
            msg.sender,
            recipient,
            amount,
            method,
            bytes32(0),
            amount,
            0
        );

        // 根据支付方式处理
        if (method == PaymentMethod.WALLET) {
            // 直接转账
            require(msg.value >= amount, "Insufficient payment");
            balances[recipient] += amount;
            payments[paymentId].completed = true;
            emit PaymentCompleted(paymentId, recipient, amount);
        }
        // X402和MULTISIG需要其他合约处理
    }

    /**
     * @dev 完成支付（由其他合约调用）
     */
    function completePayment(bytes32 paymentId) external {
        PaymentRecord storage payment = payments[paymentId];
        require(!payment.completed, "Payment already completed");
        require(
            msg.sender == paymentChannels[payment.method].channelAddress,
            "Unauthorized"
        );

        payment.completed = true;
        balances[payment.recipient] += payment.amount;
        emit PaymentCompleted(
            paymentId,
            payment.recipient,
            payment.amount
        );
    }

    /**
     * @dev 设置支付通道
     */
    function setPaymentChannel(
        PaymentMethod method,
        address channelAddress,
        bool isActive,
        uint256 priority,
        uint256 minAmount,
        uint256 maxAmount
    ) external onlyOwner {
        paymentChannels[method] = PaymentChannel({
            channelAddress: channelAddress,
            isActive: isActive,
            priority: priority,
            minAmount: minAmount,
            maxAmount: maxAmount
        });
    }

    /**
     * @dev 提取余额
     */
    function withdraw(uint256 amount) external nonReentrant {
        require(balances[msg.sender] >= amount, "Insufficient balance");
        balances[msg.sender] -= amount;
        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "Transfer failed");
    }

    /**
     * @dev 查询支付状态
     */
    function getPayment(bytes32 paymentId)
        external
        view
        returns (PaymentRecord memory)
    {
        return payments[paymentId];
    }

    // ============ 紧急控制函数 ============

    /**
     * @dev 暂停合约
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev 恢复合约
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev 紧急提款 ETH
     */
    function emergencyWithdraw(address to, uint256 amount) external onlyOwner {
        require(to != address(0), "Invalid recipient");
        require(address(this).balance >= amount, "Insufficient balance");
        (bool success, ) = payable(to).call{value: amount}("");
        require(success, "Transfer failed");
    }

    receive() external payable {
        // 接收ETH
    }
}

contracts/X402Adapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/cryptography/EIP712.sol";
import "@openzeppelin/contracts/utils/cryptography/ECDSA.sol";
import "./PaymentRouter.sol";

/**
 * @title X402Adapter
 * @dev X402协议适配器合约，用于处理X402协议支付
 * @notice 支持签名验证和批量支付处理
 */
contract X402Adapter is Ownable, ReentrancyGuard, Pausable, EIP712 {
    using SafeERC20 for IERC20;
    using ECDSA for bytes32;

    // ============ 常量 ============
    bytes32 public constant PAYMENT_TYPEHASH = keccak256(
        "Payment(bytes32 sessionId,bytes32 paymentId,address recipient,uint256 amount,uint256 expiry,uint256 chainId)"
    );
    uint256 public constant DEFAULT_GAS_SAVED = 100000;

    // X402支付会话
    struct X402Session {
        bytes32 sessionId;
        bytes32 paymentId;
        address payer;
        address recipient;
        uint256 amount;
        bytes compressedData;
        bool executed;
        uint256 createdAt;
        uint256 expiresAt;
    }

    // 事件
    event X402SessionCreated(
        bytes32 indexed sessionId,
        bytes32 indexed paymentId,
        address indexed payer,
        uint256 amount
    );

    event X402PaymentExecuted(
        bytes32 indexed sessionId,
        bytes32 indexed paymentId,
        address indexed recipient,
        uint256 amount,
        uint256 gasSaved
    );

    // 状态变量
    mapping(bytes32 => X402Session) public sessions;
    PaymentRouter public paymentRouter;
    address public relayer; // X402中继器地址

    constructor(address payable _paymentRouter) EIP712("X402Adapter", "1") {
        paymentRouter = PaymentRouter(_paymentRouter);
    }

    /**
     * @dev 创建X402支付会话
     * @param paymentId 支付ID
     * @param recipient 收款地址
     * @param amount 支付金额
     * @param compressedData 压缩的交易数据
     * @param expiresAt 过期时间
     */
    function createSession(
        bytes32 paymentId,
        address recipient,
        uint256 amount,
        bytes calldata compressedData,
        uint256 expiresAt
    ) external returns (bytes32) {
        bytes32 sessionId = keccak256(
            abi.encodePacked(
                paymentId,
                msg.sender,
                recipient,
                amount,
                block.timestamp
            )
        );

        sessions[sessionId] = X402Session({
            sessionId: sessionId,
            paymentId: paymentId,
            payer: msg.sender,
            recipient: recipient,
            amount: amount,
            compressedData: compressedData,
            executed: false,
            createdAt: block.timestamp,
            expiresAt: expiresAt
        });

        emit X402SessionCreated(sessionId, paymentId, msg.sender, amount);
        return sessionId;
    }

    /**
     * @dev 执行X402支付（由中继器调用）- 使用 EIP-712 标准签名验证
     * @param sessionId 会话ID
     * @param signature payer 的签名
     */
    function executePayment(
        bytes32 sessionId,
        bytes calldata signature
    ) external nonReentrant whenNotPaused {
        X402Session storage session = sessions[sessionId];
        require(session.sessionId != bytes32(0), "Session not found");
        require(!session.executed, "Payment already executed");
        require(block.timestamp <= session.expiresAt, "Session expired");
        require(msg.sender == relayer, "Only relayer can execute");

        // 使用 EIP-712 标准签名验证
        require(signature.length == 65, "Invalid signature length");

        // 构建 EIP-712 结构化数据哈希
        bytes32 structHash = keccak256(abi.encode(
            PAYMENT_TYPEHASH,
            session.sessionId,
            session.paymentId,
            session.recipient,
            session.amount,
            session.expiresAt,
            block.chainid
        ));

        // 使用 EIP-712 域分隔符
        bytes32 digest = _hashTypedDataV4(structHash);

        // 恢复签名者地址
        address signer = digest.recover(signature);
        require(signer == session.payer, "Invalid signature");

        // 执行转账 - 使用合约中的 ETH
        require(address(this).balance >= session.amount, "Insufficient balance");
        (bool success, ) = payable(session.recipient).call{value: session.amount}("");
        require(success, "Transfer failed");

        session.executed = true;

        // 通知PaymentRouter完成支付
        paymentRouter.completePayment(session.paymentId);

        emit X402PaymentExecuted(
            sessionId,
            session.paymentId,
            session.recipient,
            session.amount,
            DEFAULT_GAS_SAVED
        );
    }

    /**
     * @dev 获取 EIP-712 域分隔符
     */
    function getDomainSeparator() external view returns (bytes32) {
        return _domainSeparatorV4();
    }

    /**
     * @dev 设置中继器地址
     */
    function setRelayer(address _relayer) external onlyOwner {
        require(_relayer != address(0), "Invalid relayer");
        relayer = _relayer;
    }

    /**
     * @dev 暂停合约
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev 恢复合约
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev 紧急提款
     */
    function emergencyWithdraw(address to, uint256 amount) external onlyOwner {
        require(to != address(0), "Invalid recipient");
        (bool success, ) = payable(to).call{value: amount}("");
        require(success, "Transfer failed");
    }

    /**
     * @dev 查询会话信息
     */
    function getSession(bytes32 sessionId)
        external
        view
        returns (X402Session memory)
    {
        return sessions[sessionId];
    }

    /**
     * @dev 接收 ETH
     */
    receive() external payable {}
}

