// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title IAgentPayment
 * @dev Agent  - Agentrix EIP 
 * @notice  AI Agent 
 */
interface IAgentPayment {
    // ============  ============
    
    /**
     * @dev 
     */
    struct PaymentIntent {
        bytes32 intentId;           // 
        address payer;              // 
        address[] recipients;       // 
        uint256[] amounts;          // 
        bytes32 collaborationId;    // ID
        uint256 totalAmount;        // 
        uint256 createdAt;          // 
        uint256 expiresAt;          // 
        IntentStatus status;        // 
    }
    
    /**
     * @dev 
     */
    struct SplitConfig {
        address merchantWallet;     // 
        uint256 merchantAmount;     // 
        address[] agents;           // Agent 
        uint256[] agentFees;        // Agent 
        uint256 platformFee;        // 
        bytes32 sessionId;          // Session ID
        bool isDisputed;            // 
    }
    
    /**
     * @dev 
     */
    enum IntentStatus {
        CREATED,        // 
        APPROVED,       // 
        EXECUTED,       // 
        CANCELLED,      // 
        EXPIRED,        // 
        DISPUTED        // 
    }

    // ============  ============
    
    /**
     * @dev 
     */
    event IntentCreated(
        bytes32 indexed intentId,
        address indexed payer,
        uint256 totalAmount,
        bytes32 collaborationId
    );
    
    /**
     * @dev 
     */
    event IntentExecuted(
        bytes32 indexed intentId,
        uint256 totalAmount,
        uint256 recipientCount
    );
    
    /**
     * @dev 
     */
    event IntentCancelled(
        bytes32 indexed intentId,
        address indexed canceller,
        string reason
    );
    
    /**
     * @dev 
     */
    event PaymentSplit(
        bytes32 indexed intentId,
        bytes32 indexed sessionId,
        address indexed merchantWallet,
        uint256 merchantAmount,
        uint256 agentFees,
        uint256 platformFee
    );

    // ============  ============
    
    /**
     * @dev 
     * @param recipients 
     * @param amounts 
     * @param collaborationId ID
     * @param expiresIn 
     * @return intentId ID
     */
    function createIntent(
        address[] calldata recipients,
        uint256[] calldata amounts,
        bytes32 collaborationId,
        uint256 expiresIn
    ) external returns (bytes32 intentId);
    
    /**
     * @dev 
     * @param intentId ID
     * @param proof Merkle proof
     */
    function executeIntent(
        bytes32 intentId,
        bytes calldata proof
    ) external;
    
    /**
     * @dev 
     * @param intentId ID
     * @param reason 
     */
    function cancelIntent(
        bytes32 intentId,
        string calldata reason
    ) external;
    
    /**
     * @dev 
     * @param orderId ID
     * @param config 
     */
    function setSplitConfig(
        bytes32 orderId,
        SplitConfig calldata config
    ) external;
    
    /**
     * @dev 
     * @param orderId ID
     * @param amount 
     */
    function executeAutoSplit(
        bytes32 orderId,
        uint256 amount
    ) external;

    // ============  ============
    
    /**
     * @dev 
     */
    function getIntent(bytes32 intentId) external view returns (PaymentIntent memory);
    
    /**
     * @dev 
     */
    function getSplitConfig(bytes32 orderId) external view returns (SplitConfig memory);
    
    /**
     * @dev 
     */
    function getPendingBalance(address payee, address token) external view returns (uint256);
}

