pragma solidity ^0.8.20;

/**
 * @title IAgentRegistry
 * @dev Agent  - Agentrix EIP 
 * @notice  AI Agent 
 */
interface IAgentRegistry {
    // ============  ============
    
    /**
     * @dev Agent 
     */
    struct AgentInfo {
        bytes32 agentId;            // Agent 
        address owner;              // 
        address paymentWallet;      // 
        string name;                // Agent 
        string endpoint;            // API/MCP 
        bytes32[] capabilities;     // 
        uint256 trustScore;         //  (0-10000, basis points)
        uint256 totalTransactions;  // 
        uint256 totalVolume;        // 
        uint256 registeredAt;       // 
        AgentStatus status;         // 
    }
    
    /**
     * @dev Agent 
     */
    enum AgentStatus {
        PENDING,        // 
        ACTIVE,         // 
        SUSPENDED,      // 
        BLACKLISTED,    // 
        DEACTIVATED     // 
    }
    
    /**
     * @dev 
     */
    struct CapabilityDeclaration {
        bytes32 capabilityId;       // ID
        string name;                // 
        string version;             // 
        bytes32 schemaHash;         //  schema 
    }

    // ============  ============
    
    /**
     * @dev Agent 
     */
    event AgentRegistered(
        bytes32 indexed agentId,
        address indexed owner,
        string name,
        string endpoint
    );
    
    /**
     * @dev Agent 
     */
    event AgentUpdated(
        bytes32 indexed agentId,
        string field,
        bytes newValue
    );
    
    /**
     * @dev Agent 
     */
    event AgentStatusChanged(
        bytes32 indexed agentId,
        AgentStatus oldStatus,
        AgentStatus newStatus,
        string reason
    );
    
    /**
     * @dev 
     */
    event TrustScoreUpdated(
        bytes32 indexed agentId,
        uint256 oldScore,
        uint256 newScore,
        bytes32 indexed transactionId
    );
    
    /**
     * @dev 
     */
    event CapabilityDeclared(
        bytes32 indexed agentId,
        bytes32 indexed capabilityId,
        string name
    );

    // ============  ============
    
    /**
     * @dev  Agent
     * @param name Agent 
     * @param endpoint API/MCP 
     * @param paymentWallet 
     * @param capabilities 
     * @return agentId  Agent ID
     */
    function registerAgent(
        string calldata name,
        string calldata endpoint,
        address paymentWallet,
        bytes32[] calldata capabilities
    ) external returns (bytes32 agentId);
    
    /**
     * @dev  Agent 
     * @param agentId Agent ID
     * @param name  ()
     * @param endpoint  ()
     * @param paymentWallet  (address(0) )
     */
    function updateAgent(
        bytes32 agentId,
        string calldata name,
        string calldata endpoint,
        address paymentWallet
    ) external;
    
    /**
     * @dev  Agent
     * @param agentId Agent ID
     */
    function deactivateAgent(bytes32 agentId) external;
    
    /**
     * @dev  Agent 
     * @param agentId Agent ID
     * @param capability 
     */
    function declareCapability(
        bytes32 agentId,
        CapabilityDeclaration calldata capability
    ) external;

    // ============  ============
    
    /**
     * @dev  Agent ()
     * @param agentId Agent ID
     * @param reason 
     */
    function suspendAgent(bytes32 agentId, string calldata reason) external;
    
    /**
     * @dev  Agent ()
     * @param agentId Agent ID
     */
    function reinstateAgent(bytes32 agentId) external;
    
    /**
     * @dev  ()
     * @param agentId Agent ID
     * @param reason 
     */
    function blacklistAgent(bytes32 agentId, string calldata reason) external;
    
    /**
     * @dev  (/)
     * @param agentId Agent ID
     * @param transactionId ID
     * @param scoreChange  ()
     */
    function updateTrustScore(
        bytes32 agentId,
        bytes32 transactionId,
        int256 scoreChange
    ) external;

    // ============  ============
    
    /**
     * @dev  Agent 
     */
    function getAgent(bytes32 agentId) external view returns (AgentInfo memory);
    
    /**
     * @dev  Agent
     */
    function getAgentByWallet(address wallet) external view returns (bytes32 agentId);
    
    /**
     * @dev  Agent 
     */
    function getAgentCapabilities(bytes32 agentId) external view returns (bytes32[] memory);
    
    /**
     * @dev  Agent 
     */
    function hasCapability(bytes32 agentId, bytes32 capabilityId) external view returns (bool);
    
    /**
     * @dev  Agent
     */
