// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

/**
 * @title ICollaboration
 * @dev Agent  - Agentrix EIP 
 * @notice  AI Agent 
 */
interface ICollaboration {
    // ============  ============
    
    /**
     * @dev 
     */
    struct Agreement {
        bytes32 agreementId;        // 
        address initiator;          // 
        bytes32[] agentIds;         //  Agent ID 
        address[] agentWallets;     // Agent 
        uint256[] shareRatios;      //  (basis points, =10000)
        uint256 validFrom;          // 
        uint256 validUntil;         // 
        bytes32 termsHash;          //  ()
        uint8 requiredApprovals;    // 
        uint8 currentApprovals;     // 
        AgreementStatus status;     // 
    }
    
    /**
     * @dev 
     */
    enum AgreementStatus {
        DRAFT,          // 
        PENDING,        // 
        ACTIVE,         // 
        SUSPENDED,      // 
        TERMINATED,     // 
        EXPIRED         // 
    }
    
    /**
     * @dev 
     */
    struct AgreementTemplate {
        bytes32 templateId;
        string name;
        uint256[] defaultRatios;
        bytes32 termsHash;
        bool isActive;
    }

    // ============  ============
    
    /**
     * @dev 
     */
    event AgreementCreated(
        bytes32 indexed agreementId,
        address indexed initiator,
        bytes32[] agentIds,
        uint256 validUntil
    );
    
    /**
     * @dev 
     */
    event AgreementApproved(
        bytes32 indexed agreementId,
        bytes32 indexed agentId,
        address indexed approver,
        uint8 currentApprovals,
        uint8 requiredApprovals
    );
    
    /**
     * @dev 
     */
    event AgreementActivated(
        bytes32 indexed agreementId,
        uint256 activatedAt
    );
    
    /**
     * @dev 
     */
    event AgreementTerminated(
        bytes32 indexed agreementId,
        address indexed terminator,
        string reason
    );
    
    /**
     * @dev 
     */
    event ShareDistributed(
        bytes32 indexed agreementId,
        bytes32 indexed transactionId,
        uint256 totalAmount,
        uint256[] distributions
    );

    // ============  ============
    
    /**
     * @dev 
     * @param agentIds  Agent ID 
     * @param agentWallets Agent 
     * @param shareRatios  (basis points)
     * @param validUntil 
     * @param termsHash 
     * @param requiredApprovals 
     * @return agreementId ID
     */
    function createAgreement(
        bytes32[] calldata agentIds,
        address[] calldata agentWallets,
        uint256[] calldata shareRatios,
        uint256 validUntil,
        bytes32 termsHash,
        uint8 requiredApprovals
    ) external returns (bytes32 agreementId);
    
    /**
     * @dev 
     * @param agreementId ID
     * @param agentId  Agent ID
     */
    function approveAgreement(
        bytes32 agreementId,
        bytes32 agentId
    ) external;
    
    /**
     * @dev 
     * @param agreementId ID
     * @param reason 
     */
    function terminateAgreement(
        bytes32 agreementId,
        string calldata reason
    ) external;
    
    /**
     * @dev 
     * @param agreementId ID
     * @param newValidUntil 
     */
    function extendAgreement(
        bytes32 agreementId,
        uint256 newValidUntil
    ) external;

    // ============  ============
    
    /**
     * @dev 
     * @param agreementId ID
     * @param totalAmount 
     * @param transactionId ID
     * @return distributions 
     */
    function distributeByAgreement(
        bytes32 agreementId,
        uint256 totalAmount,
        bytes32 transactionId
    ) external returns (uint256[] memory distributions);
    
    /**
     * @dev 
     * @param agreementId ID
     * @param totalAmount 
     * @return distributions 
     */
    function calculateDistribution(
        bytes32 agreementId,
        uint256 totalAmount
    ) external view returns (uint256[] memory distributions);

    // ============  ============
    
    /**
     * @dev 
     */
    function createTemplate(
        string calldata name,
        uint256[] calldata defaultRatios,
        bytes32 termsHash
    ) external returns (bytes32 templateId);
    
    /**
     * @dev 
     */
    function createFromTemplate(
        bytes32 templateId,
        bytes32[] calldata agentIds,
        address[] calldata agentWallets,
        uint256 validUntil
    ) external returns (bytes32 agreementId);

    // ============  ============
    
    /**
     * @dev 
     */
    function getAgreement(bytes32 agreementId) external view returns (Agreement memory);
    
    /**
     * @dev  Agent 
     */
    function getAgentAgreements(bytes32 agentId) external view returns (bytes32[] memory);
    
    /**
     * @dev 
     */
    function isAgreementValid(bytes32 agreementId) external view returns (bool);
    
    /**
     * @dev 
     */
    function getAgreementStatus(bytes32 agreementId) external view returns (AgreementStatus);
    
    /**
     * @dev 
     */
    function hasApproved(bytes32 agreementId, address approver) external view returns (bool);
}

