contracts/PaymentRouter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title PaymentRouter
 * @dev 
 */
contract PaymentRouter is Ownable, ReentrancyGuard {
    // 
    enum PaymentMethod {
        STRIPE,
        WALLET,
        X402,
        MULTISIG
    }

    // 
    struct PaymentChannel {
        address channelAddress;
        bool isActive;
        uint256 priority; // 
        uint256 minAmount; // 
        uint256 maxAmount; // 
    }

    // 
    struct PaymentRecord {
        bytes32 paymentId;
        address payer;
        address recipient;
        uint256 amount;
        PaymentMethod method;
        bytes32 sessionId; // Session IDID
        uint256 merchantPrice; // 
        uint256 channelFee; // 
        bool completed;
        uint256 timestamp;
    }

    // 
    event PaymentRouted(
        bytes32 indexed paymentId,
        address indexed payer,
        address indexed recipient,
        uint256 amount,
        PaymentMethod method,
        bytes32 sessionId,
        uint256 merchantPrice,
        uint256 channelFee
    );

    event PaymentCompleted(
        bytes32 indexed paymentId,
        address indexed recipient,
        uint256 amount
    );

    // 
    mapping(PaymentMethod => PaymentChannel) public paymentChannels;
    mapping(bytes32 => PaymentRecord) public payments;
    mapping(address => uint256) public balances;

    /**
     * @dev Session ID
     * @param paymentId ID
     * @param recipient 
     * @param amount 
     * @param method 
     * @param sessionId Session IDID
     * @param merchantPrice 
     * @param channelFee 
     */
    function routePayment(
        bytes32 paymentId,
        address recipient,
        uint256 amount,
        PaymentMethod method,
        bytes32 sessionId,
        uint256 merchantPrice,
        uint256 channelFee
    ) external payable nonReentrant {
        require(recipient != address(0), "Invalid recipient");
        require(amount > 0, "Amount must be greater than 0");
        require(
            paymentChannels[method].isActive,
            "Payment channel not active"
        );
        require(
            amount >= paymentChannels[method].minAmount &&
                amount <= paymentChannels[method].maxAmount,
            "Amount out of range"
        );

        // 
        payments[paymentId] = PaymentRecord({
            paymentId: paymentId,
            payer: msg.sender,
            recipient: recipient,
            amount: amount,
            method: method,
            sessionId: sessionId,
            merchantPrice: merchantPrice,
            channelFee: channelFee,
            completed: false,
            timestamp: block.timestamp
        });

        emit PaymentRouted(
            paymentId,
            msg.sender,
            recipient,
            amount,
            method,
            sessionId,
            merchantPrice,
            channelFee
        );

        // 
        if (method == PaymentMethod.WALLET) {
            // 
            require(msg.value >= amount, "Insufficient payment");
            balances[recipient] += amount;
            payments[paymentId].completed = true;
            emit PaymentCompleted(paymentId, recipient, amount);
        }
        // X402MULTISIG
    }

    /**
     * @dev 
     * @param paymentId ID
     * @param recipient 
     * @param amount 
     * @param method 
     */
    function routePaymentLegacy(
        bytes32 paymentId,
        address recipient,
        uint256 amount,
        PaymentMethod method
    ) external payable nonReentrant {
        require(recipient != address(0), "Invalid recipient");
        require(amount > 0, "Amount must be greater than 0");
        require(
            paymentChannels[method].isActive,
            "Payment channel not active"
        );
        require(
            amount >= paymentChannels[method].minAmount &&
                amount <= paymentChannels[method].maxAmount,
            "Amount out of range"
        );

        // 
        payments[paymentId] = PaymentRecord({
            paymentId: paymentId,
            payer: msg.sender,
            recipient: recipient,
            amount: amount,
            method: method,
            sessionId: bytes32(0),
            merchantPrice: amount,
            channelFee: 0,
            completed: false,
            timestamp: block.timestamp
        });

        emit PaymentRouted(
            paymentId,
            msg.sender,
            recipient,
            amount,
            method,
            bytes32(0),
            amount,
            0
        );

        // 
        if (method == PaymentMethod.WALLET) {
            // 
            require(msg.value >= amount, "Insufficient payment");
            balances[recipient] += amount;
            payments[paymentId].completed = true;
            emit PaymentCompleted(paymentId, recipient, amount);
        }
        // X402MULTISIG
    }

    /**
     * @dev 
     */
    function completePayment(bytes32 paymentId) external {
        PaymentRecord storage payment = payments[paymentId];
        require(!payment.completed, "Payment already completed");
        require(
            msg.sender == paymentChannels[payment.method].channelAddress,
            "Unauthorized"
        );

        payment.completed = true;
        balances[payment.recipient] += payment.amount;
        emit PaymentCompleted(
            paymentId,
            payment.recipient,
            payment.amount
        );
    }

    /**
     * @dev 
     */
    function setPaymentChannel(
        PaymentMethod method,
        address channelAddress,
        bool isActive,
        uint256 priority,
        uint256 minAmount,
        uint256 maxAmount
    ) external onlyOwner {
        paymentChannels[method] = PaymentChannel({
            channelAddress: channelAddress,
            isActive: isActive,
            priority: priority,
            minAmount: minAmount,
            maxAmount: maxAmount
        });
    }

    /**
     * @dev 
     */
    function withdraw(uint256 amount) external nonReentrant {
        require(balances[msg.sender] >= amount, "Insufficient balance");
        balances[msg.sender] -= amount;
        (bool success, ) = payable(msg.sender).call{value: amount}("");
        require(success, "Transfer failed");
    }

    /**
     * @dev 
     */
    function getPayment(bytes32 paymentId)
        external
        view
        returns (PaymentRecord memory)
    {
        return payments[paymentId];
    }

    receive() external payable {
        // ETH
    }
}

contracts/X402Adapter.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "./PaymentRouter.sol";

/**
 * @title X402Adapter
 * @dev X402X402
 * @notice 
 */
contract X402Adapter is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;
    // X402
    struct X402Session {
        bytes32 sessionId;
        bytes32 paymentId;
        address payer;
        address recipient;
        uint256 amount;
        bytes compressedData;
        bool executed;
        uint256 createdAt;
        uint256 expiresAt;
    }

    // 
    event X402SessionCreated(
        bytes32 indexed sessionId,
        bytes32 indexed paymentId,
        address indexed payer,
        uint256 amount
    );

    event X402PaymentExecuted(
        bytes32 indexed sessionId,
        bytes32 indexed paymentId,
        address indexed recipient,
        uint256 amount,
        uint256 gasSaved
    );

    // 
    mapping(bytes32 => X402Session) public sessions;
    PaymentRouter public paymentRouter;
    address public relayer; // X402

    constructor(address payable _paymentRouter) {
        paymentRouter = PaymentRouter(_paymentRouter);
    }

    /**
     * @dev X402
     * @param paymentId ID
     * @param recipient 
     * @param amount 
     * @param compressedData 
     * @param expiresAt 
     */
    function createSession(
        bytes32 paymentId,
        address recipient,
        uint256 amount,
        bytes calldata compressedData,
        uint256 expiresAt
    ) external returns (bytes32) {
        bytes32 sessionId = keccak256(
            abi.encodePacked(
                paymentId,
                msg.sender,
                recipient,
                amount,
                block.timestamp
            )
        );

        sessions[sessionId] = X402Session({
            sessionId: sessionId,
            paymentId: paymentId,
            payer: msg.sender,
            recipient: recipient,
            amount: amount,
            compressedData: compressedData,
            executed: false,
            createdAt: block.timestamp,
            expiresAt: expiresAt
        });

        emit X402SessionCreated(sessionId, paymentId, msg.sender, amount);
        return sessionId;
    }

    /**
     * @dev X402
     * @param sessionId ID
     * @param signature payer 
     */
    function executePayment(
        bytes32 sessionId,
        bytes calldata signature
    ) external nonReentrant whenNotPaused {
        X402Session storage session = sessions[sessionId];
        require(session.sessionId != bytes32(0), "Session not found");
        require(!session.executed, "Payment already executed");
        require(block.timestamp <= session.expiresAt, "Session expired");
        require(msg.sender == relayer, "Only relayer can execute");

        //  -  payer 
        require(signature.length == 65, "Invalid signature length");

        bytes32 messageHash = keccak256(
            abi.encodePacked(
                "\x19Ethereum Signed Message:\n32",
                keccak256(
                    abi.encodePacked(
                        session.sessionId,
                        session.paymentId,
                        session.recipient,
                        session.amount,
                        block.chainid
                    )
                )
            )
        );

        // 
        address signer = recoverSigner(messageHash, signature);
        require(signer == session.payer, "Invalid signature");

        //  -  ETH
        require(address(this).balance >= session.amount, "Insufficient balance");
        (bool success, ) = payable(session.recipient).call{value: session.amount}("");
        require(success, "Transfer failed");

        session.executed = true;

        // PaymentRouter
        paymentRouter.completePayment(session.paymentId);

        // Gas
        uint256 gasSaved = 100000; // 

        emit X402PaymentExecuted(
            sessionId,
            session.paymentId,
            session.recipient,
            session.amount,
            gasSaved
        );
    }

    /**
     * @dev 
     */
    function recoverSigner(bytes32 messageHash, bytes memory signature)
        internal
        pure
        returns (address)
    {
        bytes32 r;
        bytes32 s;
        uint8 v;

        assembly {
            r := mload(add(signature, 32))
            s := mload(add(signature, 64))
            v := byte(0, mload(add(signature, 96)))
        }

        if (v < 27) {
            v += 27;
        }

        require(v == 27 || v == 28, "Invalid signature");

        return ecrecover(messageHash, v, r, s);
    }

    /**
     * @dev 
     */
    function setRelayer(address _relayer) external onlyOwner {
        require(_relayer != address(0), "Invalid relayer");
        relayer = _relayer;
    }

    /**
     * @dev 
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev 
     */
    function unpause() external onlyOwner {
        _unpause();
    }

    /**
     * @dev 
     */
    function emergencyWithdraw(address to, uint256 amount) external onlyOwner {
        require(to != address(0), "Invalid recipient");
        (bool success, ) = payable(to).call{value: amount}("");
        require(success, "Transfer failed");
    }

    /**
     * @dev 
     */
    function getSession(bytes32 sessionId)
        external
        view
        returns (X402Session memory)
    {
        return sessions[sessionId];
    }

    /**
     * @dev  ETH
     */
    receive() external payable {}
}

